      setCopyStatus('test-failed');
    }
    
    setTimeout(() => setCopyStatus(''), 2000);
  };

  // ä¸€é”®ç¿»è¯‘æ‰€æœ‰æœªç¿»è¯‘çš„æ ‡ç­¾
  const translateAllTags = async () => {
    const untranslatedTags = selectedTags.filter(tag => {
