    });

    if (untranslatedTags.length === 0) {
      setCopyStatus('no-untranslated');
      setTimeout(() => setCopyStatus(''), 2000);
      return;
    }

    setCopyStatus('translating');
    
    try {
      const texts = untranslatedTags.map(tag => parseTag(tag).text);
      const results = await batchTranslate(texts, {
        translator: selectedTranslator,
        targetLang: 'zh',
        sourceLang: 'auto'
      });

      const newTranslations = {};
      results.forEach(result => {
        if (result.success) {
          newTranslations[result.original] = result.translated;
        }
      });

      setTranslatedTags(prev => ({ ...prev, ...newTranslations }));
      setCopyStatus('translate-success');
    } catch (error) {
      console.error('æ‰¹é‡ç¿»è¯‘å¤±è´¥:', error);
      setCopyStatus('translate-error');
    }
    
    setTimeout(() => setCopyStatus(''), 2000);
  };

  // ç¿»è¯‘å•ä¸ªæ ‡ç­¾
  const translateSingleTag = async (tag) => {
    // æå–çº¯æ–‡æœ¬è¿›è¡Œç¿»è¯‘
    const parsed = parseTag(tag);
    const pureText = parsed.text;
    
    if (translatedTags[pureText]) {
      return translatedTags[pureText];
    }
    
    try {
      const translated = await translateTag(pureText, {
        translator: selectedTranslator,
        targetLang: 'zh',
        sourceLang: 'auto'
      });
      setTranslatedTags(prev => ({ ...prev, [pureText]: translated }));
      return translated;
    } catch (error) {
      console.error('ç¿»è¯‘å¤±è´¥:', error);
      return pureText;
    }
  };

  // å¤åˆ¶æ ‡ç­¾
  const copyTag = async (tag) => {
    const success = await copyToClipboard(tag);
    setCopyStatus(success ? 'copied' : 'error');
    setTimeout(() => setCopyStatus(''), 2000);
  };

  // æ”¶è—æ ‡ç­¾
  const favoriteTag = (tag) => {
    const parsed = parseTag(tag);
    const tagObj = { en: parsed.text, cn: translatedTags[parsed.text] || parsed.text, frequency: 80 };
