sourcesContent:
import React, { useState, useCallback } from 'react';
import { Search, Wand2, BookOpen, Image, Settings, Heart, Copy, Download, Sparkles, Tag, Scale, Lightbulb, Languages, Upload, CheckCircle, AlertCircle } from 'lucide-react';

const AIPaintingAssistant = () => {
  const [activeTab, setActiveTab] = useState('generator');
  const [inputText, setInputText] = useState('');
  const [generatedPrompt, setGeneratedPrompt] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedStyle, setSelectedStyle] = useState('');
  const [isGenerating, setIsGenerating] = useState(false);
  const [copyStatus, setCopyStatus] = useState('');

  const tabs = [
    { id: 'generator', icon: Wand2, label: '提示词生成' },
    { id: 'library', icon: Search, label: '提示词库' },
    { id: 'extractor', icon: Image, label: '图像提取' },
    { id: 'tools', icon: Settings, label: '辅助工具' },
    { id: 'tutorial', icon: BookOpen, label: '学习教程' }
  ];

  const promptCategories = [
    { name: '人物', count: 1250, color: 'bg-blue-100 text-blue-800', description: '人物角色、表情、动作' },
    { name: '场景', count: 892, color: 'bg-green-100 text-green-800', description: '背景环境、建筑、风景' },
    { name: '风格', count: 456, color: 'bg-purple-100 text-purple-800', description: '艺术风格、画风流派' },
    { name: '效果', count: 334, color: 'bg-orange-100 text-orange-800', description: '光影、质感、特效' },
    { name: '构图', count: 198, color: 'bg-pink-100 text-pink-800', description: '镜头角度、画面布局' }
  ];

  const styles = [
    { name: '写实', prompt: 'photorealistic, ultra realistic, high quality' },
    { name: '动漫', prompt: 'anime style, manga style, cel shading' },
    { name: '油画', prompt: 'oil painting, classical art, painterly' },
    { name: '水彩', prompt: 'watercolor, soft colors, flowing' },
    { name: '素描', prompt: 'pencil sketch, charcoal drawing, monochrome' }
  ];

  const tools = [
    { icon: Scale, name: '权重调节', desc: '为提示词添加权重标记', color: 'text-orange-600' },
    { icon: Lightbulb, name: '灵感生成', desc: '基于主题生成创作灵感', color: 'text-yellow-600' },
    { icon: Languages, name: '翻译工具', desc: '中英文提示词互译', color: 'text-blue-600' },
    { icon: Tag, name: '标签补全', desc: '智能提示标签建议', color: 'text-green-600' }
  ];

  const hotPrompts = [
    { en: 'masterpiece, best quality, ultra detailed', cn: '杰作，最佳质量，超详细', usage: '89%', category: '质量' },
    { en: 'beautiful girl, cute, kawaii', cn: '美丽女孩，可爱，萌', usage: '76%', category: '人物' },
    { en: 'cinematic lighting, dramatic shadows', cn: '电影级光照，戏剧性阴影', usage: '65%', category: '效果' },
    { en: 'fantasy landscape, magical forest', cn: '奇幻风景，魔法森林', usage: '58%', category: '场景' },
    { en: 'anime style, cel shading', cn: '动漫风格，赛璐珞着色', usage: '52%', category: '风格' }
  ];

  const tutorials = [
    { title: 'AI绘画入门基础', level: '新手', time: '30分钟', progress: 0, description: '了解AI绘画基本概念和工具' },
    { title: '提示词编写技巧', level: '初级', time: '45分钟', progress: 60, description: '掌握高效提示词写作方法' },
    { title: '高级构图方法', level: '进阶', time: '60分钟', progress: 100, description: '学习专业构图技巧' },
    { title: '风格控制技术', level: '进阶', time: '40分钟', progress: 0, description: '控制图片风格的高级技巧' },
    { title: '参数调优指南', level: '高级', time: '90分钟', progress: 30, description: '深入理解生成参数' },
    { title: '工作流程优化', level: '高级', time: '75分钟', progress: 0, description: '提高创作效率的工作流' }
  ];

  const handleGenerate = useCallback(async () => {
    if (!inputText.trim()) return;
    
    setIsGenerating(true);
    
    // 模拟AI生成延迟
    await new Promise(resolve => setTimeout(resolve, 2000));
    
    const qualityPrompts = [
      "masterpiece, best quality, highly detailed, ultra-detailed, 8k wallpaper, extremely delicate and beautiful",
      "professional photography, studio lighting, sharp focus, vivid colors, cinematic composition, depth of field",
      "digital art, concept art, smooth, sharp focus, illustration, trending on artstation"
    ];
    
    const stylePrompt = selectedStyle ? styles.find(s => s.name === selectedStyle)?.prompt || '' : '';
    const qualityPrompt = qualityPrompts[Math.floor(Math.random() * qualityPrompts.length)];
    
    const generated = [inputText, stylePrompt, qualityPrompt].filter(Boolean).join(', ');
    setGeneratedPrompt(generated);
    setIsGenerating(false);
  }, [inputText, selectedStyle]);

  const handleCopy = useCallback(async (text) => {
    try {
      await navigator.clipboard.writeText(text);
      setCopyStatus('copied');
      setTimeout(() => setCopyStatus(''), 2000);
    } catch (err) {
      setCopyStatus('error');
      setTimeout(() => setCopyStatus(''), 2000);
    }
  }, []);

  const renderContent = () => {
    switch(activeTab) {
      case 'generator':
        return (
          <div className="space-y-6 animate-fade-in">
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                <Wand2 className="mr-2 text-purple-600" size={20} />
                i-Prompt 智能生成器
              </h2>
              
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    描述你想要的画面
                  </label>
                  <textarea
                    value={inputText}
                    onChange={(e) => setInputText(e.target.value)}
                    placeholder="例如：一个可爱的猫女孩，穿着白色连衣裙，在樱花树下..."
                    className="w-full h-24 px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent resize-none transition-all"
                  />
                </div>
                
                <div className="space-y-2">
                  <span className="text-sm font-medium text-gray-700">选择风格：</span>
                  <div className="flex flex-wrap gap-2">
                    {styles.map(style => (
                      <button 
                        key={style.name} 
                        onClick={() => setSelectedStyle(selectedStyle === style.name ? '' : style.name)}
                        className={`px-3 py-1 text-xs rounded-full transition-all ${
                          selectedStyle === style.name 
                            ? 'bg-purple-600 text-white' 
                            : 'bg-gray-100 hover:bg-purple-100 text-gray-700'
                        }`}
                      >
                        {style.name}
                      </button>
                    ))}
                  </div>
                </div>
                
                <button
                  onClick={handleGenerate}
                  disabled={!inputText.trim() || isGenerating}
                  className="w-full bg-gradient-to-r from-purple-600 to-blue-600 text-white py-3 px-6 rounded-lg font-medium hover:shadow-lg transition-all disabled:opacity-50 disabled:cursor-not-allowed relative"
                >
                  {isGenerating ? (
                    <>
                      <div className="animate-spin inline-block w-4 h-4 border-2 border-white border-t-transparent rounded-full mr-2"></div>
                      生成中...
                    </>
                  ) : (
                    <>
                      <Sparkles className="inline mr-2" size={16} />
                      生成提示词
                    </>
                  )}
                </button>
              </div>
              
              {generatedPrompt && (
                <div className="mt-6 p-4 bg-gray-50 rounded-lg animate-slide-up">
                  <div className="flex justify-between items-start mb-2">
                    <h3 className="font-medium text-gray-900">生成的提示词</h3>
                    <div className="flex gap-2">
                      <button 
                        onClick={() => handleCopy(generatedPrompt)}
                        className="p-1 hover:bg-gray-200 rounded transition-colors relative"
                      >
                        {copyStatus === 'copied' ? (
                          <CheckCircle size={16} className="text-green-600" />
                        ) : copyStatus === 'error' ? (
                          <AlertCircle size={16} className="text-red-600" />
                        ) : (
                          <Copy size={16} className="text-gray-600" />
                        )}
                      </button>
                      <button className="p-1 hover:bg-gray-200 rounded transition-colors">
                        <Heart size={16} className="text-gray-600" />
                      </button>
                    </div>
                  </div>
                  <p className="text-sm text-gray-700 leading-relaxed">{generatedPrompt}</p>
                </div>
              )}
            </div>
            
            {/* 快速提示 */}
            <div className="bg-blue-50 rounded-xl p-4 border border-blue-200">
              <h3 className="font-medium text-blue-900 mb-2">💡 i-Prompt 使用技巧</h3>
              <ul className="text-sm text-blue-800 space-y-1">
                <li>• 详细描述能生成更精确的提示词</li>
                <li>• 选择合适的风格会自动添加相关标签</li>
                <li>• 可以多次生成，选择最满意的结果</li>
                <li>• IP助手会智能优化你的创意描述</li>
              </ul>
            </div>
          </div>
        );
        
      case 'library':
        return (
          <div className="space-y-6 animate-fade-in">
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                <Search className="mr-2 text-blue-600" size={20} />
                提示词库
              </h2>
              
              <div className="mb-6">
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
                  <input
                    type="text"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    placeholder="搜索提示词、标签或效果..."
                    className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                  />
                </div>
              </div>
              
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-6">
                {promptCategories.map(category => (
                  <div key={category.name} className="p-4 border border-gray-200 rounded-lg hover:shadow-md transition-all cursor-pointer group">
                    <div className="flex justify-between items-center mb-2">
                      <h3 className="font-medium text-gray-900 group-hover:text-blue-600 transition-colors">{category.name}</h3>
                      <span className={`px-2 py-1 text-xs rounded-full ${category.color}`}>
                        {category.count}
                      </span>
                    </div>
                    <p className="text-xs text-gray-500">{category.description}</p>
                  </div>
                ))}
              </div>
              
              <div className="space-y-3">
                <h3 className="font-medium text-gray-900">热门提示词</h3>
                {hotPrompts.map((item, index) => (
                  <div key={index} className="p-3 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors">
                    <div className="flex justify-between items-start mb-1">
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-1">
                          <p className="text-sm font-medium text-gray-900">{item.en}</p>
                          <span className="text-xs px-2 py-0.5 bg-gray-200 text-gray-600 rounded">{item.category}</span>
                        </div>
                        <p className="text-xs text-gray-600">{item.cn}</p>
                      </div>
                      <div className="flex items-center gap-2 ml-4">
                        <span className="text-xs text-gray-500">{item.usage}</span>
                        <button 
                          onClick={() => handleCopy(item.en)}
                          className="p-1 hover:bg-gray-200 rounded transition-colors"
                        >
                          <Copy size={14} className="text-gray-600" />
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        );
        
      case 'extractor':
        return (
          <div className="space-y-6 animate-fade-in">
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                <Image className="mr-2 text-green-600" size={20} />
                图像信息提取器
              </h2>
              
              <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center hover:border-green-400 transition-colors">
                <Upload className="mx-auto mb-4 text-gray-400" size={48} />
                <p className="text-lg font-medium text-gray-900 mb-2">拖拽或点击上传图片</p>
                <p className="text-sm text-gray-600 mb-4">支持 PNG, JPG, WEBP 格式</p>
                <button className="bg-green-600 text-white px-6 py-2 rounded-lg hover:bg-green-700 transition-colors">
                  选择文件
                </button>
              </div>
              
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-6">
                <div className="p-4 bg-blue-50 rounded-lg">
                  <h3 className="font-medium text-blue-900 mb-2">支持格式</h3>
                  <ul className="text-sm text-blue-800 space-y-1">
                    <li>• Stable Diffusion 生成的图片</li>
                    <li>• ComfyUI 工作流图片</li>
                    <li>• Midjourney 图片（部分信息）</li>
                    <li>• NovelAI 生成图片</li>
                  </ul>
                </div>
                <div className="p-4 bg-green-50 rounded-lg">
                  <h3 className="font-medium text-green-900 mb-2">提取信息</h3>
                  <ul className="text-sm text-green-800 space-y-1">
                    <li>• 正向和反向提示词</li>
                    <li>• 生成参数（采样器、步数等）</li>
                    <li>• 模型信息和版本</li>
                    <li>• 种子值和尺寸信息</li>
                  </ul>
                </div>
              </div>
            </div>
          </div>
        );
        
      case 'tools':
        return (
          <div className="space-y-6 animate-fade-in">
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                <Settings className="mr-2 text-orange-600" size={20} />
                辅助工具
              </h2>
              
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {tools.map((tool, index) => (
                  <div key={index} className="p-4 border border-gray-200 rounded-lg hover:shadow-md transition-all cursor-pointer group">
                    <div className="flex items-start">
                      <tool.icon className={`mr-3 mt-1 ${tool.color} group-hover:scale-110 transition-transform`} size={20} />
                      <div>
                        <h3 className="font-medium text-gray-900 mb-1 group-hover:text-gray-700">{tool.name}</h3>
                        <p className="text-sm text-gray-600">{tool.desc}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
              
              {/* 工具预览区域 */}
              <div className="mt-6 p-4 bg-gray-50 rounded-lg">
                <h3 className="font-medium text-gray-900 mb-2">快速操作</h3>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
                  <button className="p-2 bg-white border border-gray-200 rounded text-sm hover:bg-gray-100 transition-colors">
                    权重增强 (word:1.2)
                  </button>
                  <button className="p-2 bg-white border border-gray-200 rounded text-sm hover:bg-gray-100 transition-colors">
                    权重减弱 [word:0.8]
                  </button>
                  <button className="p-2 bg-white border border-gray-200 rounded text-sm hover:bg-gray-100 transition-colors">
                    随机灵感
                  </button>
                </div>
              </div>
            </div>
          </div>
        );
        
      case 'tutorial':
        return (
          <div className="space-y-6 animate-fade-in">
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                <BookOpen className="mr-2 text-indigo-600" size={20} />
                学习教程
              </h2>
              
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {tutorials.map((tutorial, index) => (
                  <div key={index} className="p-4 border border-gray-200 rounded-lg hover:shadow-md transition-all cursor-pointer group">
                    <h3 className="font-medium text-gray-900 mb-2 group-hover:text-indigo-600 transition-colors">{tutorial.title}</h3>
                    <p className="text-sm text-gray-600 mb-3 line-clamp-2">{tutorial.description}</p>
                    <div className="flex justify-between items-center mb-2">
                      <span className={`px-2 py-1 text-xs rounded-full ${
                        tutorial.level === '新手' ? 'bg-green-100 text-green-800' :
                        tutorial.level === '初级' ? 'bg-blue-100 text-blue-800' :
                        tutorial.level === '进阶' ? 'bg-orange-100 text-orange-800' :
                        'bg-red-100 text-red-800'
                      }`}>
                        {tutorial.level}
                      </span>
                      <span className="text-xs text-gray-500">{tutorial.time}</span>
                    </div>
                    {tutorial.progress > 0 && (
                      <div className="w-full bg-gray-200 rounded-full h-2">
                        <div 
                          className="bg-indigo-600 h-2 rounded-full transition-all" 
                          style={{width: `${tutorial.progress}%`}}
                        ></div>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </div>
          </div>
        );
        
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <div className="flex items-center">
                <div className="w-8 h-8 bg-gradient-to-r from-purple-600 to-blue-600 rounded-lg flex items-center justify-center">
                  <span className="text-white font-bold text-sm">IP</span>
                </div>
                <h1 className="ml-3 text-xl font-bold text-gray-900">i-Prompt</h1>
                <span className="ml-2 px-2 py-0.5 text-xs bg-purple-100 text-purple-600 rounded-full">Beta</span>
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              <button className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100 transition-colors">
                <Heart size={20} />
              </button>
              <button className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100 transition-colors">
                <Download size={20} />
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Navigation */}
      <nav className="bg-white border-b border-gray-200 sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex space-x-8 overflow-x-auto">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex items-center py-4 px-1 border-b-2 font-medium text-sm whitespace-nowrap transition-all ${
                  activeTab === tab.id
                    ? 'border-purple-500 text-purple-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                <tab.icon className="mr-2" size={16} />
                {tab.label}
              </button>
            ))}
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {renderContent()}
      </main>

      {/* Footer */}
      <footer className="bg-white border-t border-gray-200 mt-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex flex-col md:flex-row justify-between items-center">
            <p className="text-sm text-gray-500 mb-4 md:mb-0">© 2025 i-Prompt. 智能提示词助手，让AI创作更简单.</p>
            <div className="flex space-x-6 text-sm text-gray-500">
              <a href="#" className="hover:text-gray-700 transition-colors">帮助中心</a>
              <a href="#" className="hover:text-gray-700 transition-colors">反馈建议</a>
              <a href="#" className="hover:text-gray-700 transition-colors">关于我们</a>
            </div>
          </div>
        </div>
      </footer>
      
      {/* 复制状态提示 */}
      {copyStatus && (
        <div className={`fixed bottom-4 right-4 px-4 py-2 rounded-lg text-white text-sm transition-opacity ${
          copyStatus === 'copied' ? 'bg-green-600' : 'bg-red-600'
        }`}>
          {copyStatus === 'copied' ? '✅ 已复制到剪贴板' : '❌ 复制失败'}
        </div>
      )}
    </div>
  );
};

export default AIPaintingAssistant;
