import React, { useState, useCallback } from 'react';
import { Search, Wand2, BookOpen, Image, Settings, Heart, Copy, Download, Sparkles, Tag, Scale, Lightbulb, Languages, Upload, CheckCircle, AlertCircle } from 'lucide-react';

const AIPaintingAssistant = () => {
  const [activeTab, setActiveTab] = useState('generator');
  const [inputText, setInputText] = useState('');
  const [generatedPrompt, setGeneratedPrompt] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedStyle, setSelectedStyle] = useState('');
  const [isGenerating, setIsGenerating] = useState(false);
  const [copyStatus, setCopyStatus] = useState('');
  const [showQuickTags, setShowQuickTags] = useState(false);

  // 快速标签 - 中英文对照
  const quickTags = [
    { 
      category: 'Quality 质量', 
      tags: [
        { en: 'masterpiece', cn: '杰作' },
        { en: 'best quality', cn: '最佳质量' },
        { en: 'ultra detailed', cn: '超详细' },
        { en: '8k wallpaper', cn: '8K壁纸' },
        { en: 'high resolution', cn: '高分辨率' },
        { en: 'professional', cn: '专业级' }
      ] 
    },
    { 
      category: 'Style 风格', 
      tags: [
        { en: 'anime style', cn: '动漫风格' },
        { en: 'realistic', cn: '写实风格' },
        { en: 'oil painting', cn: '油画' },
        { en: 'watercolor', cn: '水彩' },
        { en: 'sketch', cn: '素描' },
        { en: 'digital art', cn: '数字艺术' }
      ] 
    },
    { 
      category: 'Lighting 光照', 
      tags: [
        { en: 'cinematic lighting', cn: '电影级光照' },
        { en: 'soft lighting', cn: '柔和光照' },
        { en: 'dramatic lighting', cn: '戏剧性光照' },
        { en: 'golden hour', cn: '黄金时刻' },
        { en: 'studio lighting', cn: '摄影棚光照' },
        { en: 'natural lighting', cn: '自然光照' }
      ] 
    },
    { 
      category: 'Composition 构图', 
      tags: [
        { en: 'perfect composition', cn: '完美构图' },
        { en: 'rule of thirds', cn: '三分法则' },
        { en: 'dynamic angle', cn: '动态角度' },
        { en: 'close-up', cn: '特写镜头' },
        { en: 'wide shot', cn: '远景镜头' },
        { en: 'bird eye view', cn: '鸟瞰视角' }
      ] 
    },
    { 
      category: 'Effects 效果', 
      tags: [
        { en: 'depth of field', cn: '景深效果' },
        { en: 'bokeh', cn: '虚化效果' },
        { en: 'sharp focus', cn: '锐利对焦' },
        { en: 'motion blur', cn: '运动模糊' },
        { en: 'lens flare', cn: '镜头光晕' },
        { en: 'chromatic aberration', cn: '色差效果' }
      ] 
    }
  ];

  const tabs = [
    { id: 'generator', icon: Wand2, label: '提示词生成' },
    { id: 'library', icon: Search, label: '提示词库' },
    { id: 'extractor', icon: Image, label: '图像提取' },
    { id: 'tools', icon: Settings, label: '辅助工具' },
    { id: 'tutorial', icon: BookOpen, label: '学习教程' }
  ];

  const promptCategories = [
    { name: 'Character 人物', count: 1250, color: 'bg-blue-100 text-blue-800', description: '人物角色、表情、动作 | Characters, expressions, actions' },
    { name: 'Scene 场景', count: 892, color: 'bg-green-100 text-green-800', description: '背景环境、建筑、风景 | Background, architecture, landscapes' },
    { name: 'Style 风格', count: 456, color: 'bg-purple-100 text-purple-800', description: '艺术风格、画风流派 | Art styles, artistic movements' },
    { name: 'Effects 效果', count: 334, color: 'bg-orange-100 text-orange-800', description: '光影、质感、特效 | Lighting, textures, visual effects' },
    { name: 'Composition 构图', count: 198, color: 'bg-pink-100 text-pink-800', description: '镜头角度、画面布局 | Camera angles, layout composition' }
  ];

  const styles = [
    { name: 'Photorealistic 写实', prompt: 'photorealistic, ultra realistic, high quality' },
    { name: 'Anime 动漫', prompt: 'anime style, manga style, cel shading' },
    { name: 'Oil Painting 油画', prompt: 'oil painting, classical art, painterly' },
    { name: 'Watercolor 水彩', prompt: 'watercolor, soft colors, flowing' },
    { name: 'Sketch 素描', prompt: 'pencil sketch, charcoal drawing, monochrome' }
  ];

  const tools = [
    { icon: Scale, name: '权重调节', desc: '为提示词添加权重标记', color: 'text-orange-600' },
    { icon: Lightbulb, name: '灵感生成', desc: '基于主题生成创作灵感', color: 'text-yellow-600' },
    { icon: Languages, name: '翻译工具', desc: '中英文提示词互译', color: 'text-blue-600' },
    { icon: Tag, name: '标签补全', desc: '智能提示标签建议', color: 'text-green-600' }
  ];

  const hotPrompts = [
    { en: 'masterpiece, best quality, ultra detailed', cn: '杰作，最佳质量，超详细', usage: '89%', category: 'Quality', popularity: 95 },
    { en: 'beautiful girl, cute, kawaii', cn: '美丽女孩，可爱，萌', usage: '76%', category: 'Character', popularity: 88 },
    { en: 'cinematic lighting, dramatic shadows', cn: '电影级光照，戏剧性阴影', usage: '65%', category: 'Effects', popularity: 82 },
    { en: 'fantasy landscape, magical forest', cn: '奇幻风景，魔法森林', usage: '58%', category: 'Scene', popularity: 75 },
    { en: 'anime style, cel shading', cn: '动漫风格，赛璐珞着色', usage: '52%', category: 'Style', popularity: 70 },
    { en: 'photorealistic, hyperrealistic', cn: '照片级写实，超写实', usage: '48%', category: 'Style', popularity: 68 },
    { en: 'dynamic pose, action shot', cn: '动态姿势，动作镜头', usage: '45%', category: 'Composition', popularity: 65 },
    { en: 'soft lighting, golden hour', cn: '柔和光照，黄金时刻', usage: '42%', category: 'Effects', popularity: 62 },
    { en: 'detailed background, rich colors', cn: '详细背景，丰富色彩', usage: '39%', category: 'Quality', popularity: 60 },
    { en: 'portrait, close-up, shallow depth of field', cn: '肖像，特写，浅景深', usage: '36%', category: 'Composition', popularity: 58 }
  ];

  const tutorials = [
    { title: 'AI绘画入门基础', level: '新手', time: '30分钟', progress: 0, description: '了解AI绘画基本概念和工具' },
    { title: '提示词编写技巧', level: '初级', time: '45分钟', progress: 60, description: '掌握高效提示词写作方法' },
    { title: '高级构图方法', level: '进阶', time: '60分钟', progress: 100, description: '学习专业构图技巧' },
    { title: '风格控制技术', level: '进阶', time: '40分钟', progress: 0, description: '控制图片风格的高级技巧' },
    { title: '参数调优指南', level: '高级', time: '90分钟', progress: 30, description: '深入理解生成参数' },
    { title: '工作流程优化', level: '高级', time: '75分钟', progress: 0, description: '提高创作效率的工作流' }
  ];

  // 智能提示词生成逻辑
  const generateSmartPrompt = (input, style) => {
    const qualityTags = [
      "masterpiece, best quality, ultra detailed, extremely detailed",
      "8k wallpaper, highly detailed, professional quality",
      "absurdres, incredible detail, finely detailed",
      "amazing quality, ultra high res, detailed background"
    ];

    const lightingEffects = [
      "cinematic lighting, dramatic lighting",
      "soft lighting, natural lighting",
      "studio lighting, professional lighting",
      "golden hour lighting, warm lighting"
    ];

    const compositionTags = [
      "perfect composition, rule of thirds",
      "dynamic angle, interesting composition",
      "centered composition, balanced layout",
      "artistic composition, visual harmony"
    ];

    // 根据输入内容智能添加相关标签
    let enhancedPrompt = input;
    
    // 检测人物相关关键词
    if (/女孩|男孩|人物|角色|美女|帅哥|少女|boy|girl|character/i.test(input)) {
      enhancedPrompt += ", beautiful detailed eyes, detailed face, perfect anatomy";
    }
    
    // 检测场景相关关键词
    if (/风景|森林|海边|城市|建筑|landscape|forest|ocean|city|building/i.test(input)) {
      enhancedPrompt += ", wide shot, scenic view, atmospheric perspective";
    }
    
    // 添加风格
    if (style) {
      const styleData = styles.find(s => s.name === style);
      if (styleData) {
        enhancedPrompt += ", " + styleData.prompt;
      }
    }
    
    // 随机添加质量、光照和构图标签
    const randomQuality = qualityTags[Math.floor(Math.random() * qualityTags.length)];
    const randomLighting = lightingEffects[Math.floor(Math.random() * lightingEffects.length)];
    const randomComposition = compositionTags[Math.floor(Math.random() * compositionTags.length)];
    
    return `${enhancedPrompt}, ${randomQuality}, ${randomLighting}, ${randomComposition}`;
  };

  const handleGenerate = useCallback(async () => {
    if (!inputText.trim()) return;
    
    setIsGenerating(true);
    
    // 模拟AI生成延迟
    await new Promise(resolve => setTimeout(resolve, 1500));
    
    const generated = generateSmartPrompt(inputText, selectedStyle);
    setGeneratedPrompt(generated);
    setIsGenerating(false);
  }, [inputText, selectedStyle]);

  const handleCopy = useCallback(async (text) => {
    try {
      await navigator.clipboard.writeText(text);
      setCopyStatus('copied');
      setTimeout(() => setCopyStatus(''), 2000);
    } catch (err) {
      setCopyStatus('error');
      setTimeout(() => setCopyStatus(''), 2000);
    }
  }, []);

  const insertTag = useCallback((tag) => {
    const currentText = inputText;
    const tagText = typeof tag === 'object' ? tag.en : tag;
    const newText = currentText ? `${currentText}, ${tagText}` : tagText;
    setInputText(newText);
  }, [inputText]);

  const renderContent = () => {
    switch(activeTab) {
      case 'generator':
        return (
          <div className="space-y-6 animate-fade-in">
            <div className="bg-white/80 backdrop-blur-sm rounded-xl shadow-lg border border-purple-100/50 p-6 hover:shadow-xl transition-all duration-300">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                <Wand2 className="mr-2 text-purple-600" size={20} />
                i-Prompt 智能生成器
                <span className="ml-auto text-xs bg-gradient-to-r from-purple-500 to-blue-500 text-white px-2 py-1 rounded-full">智能AI</span>
              </h2>
              
              <div className="space-y-4">
                <div>
                  <div className="flex justify-between items-center mb-2">
                    <label className="block text-sm font-medium text-gray-700">
                      描述你想要的画面
                    </label>
                    <span className="text-xs text-gray-500">
                      {inputText.length}/500 字符
                    </span>
                  </div>
                  <textarea
                    value={inputText}
                    onChange={(e) => setInputText(e.target.value)}
                    placeholder="例如：一个可爱的猫女孩，穿着白色连衣裙，在樱花树下..."
                    maxLength={500}
                    className="w-full h-28 px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent resize-none transition-all bg-gradient-to-br from-white to-purple-50/30 hover:shadow-md"
                  />
                  {inputText.length > 0 && (
                    <div className="mt-2 text-xs text-gray-600">
                      <span className="font-medium">预览：</span>
                      智能检测到关键词：
                      {inputText.match(/女孩|男孩|人物|角色|美女|帅哥|少女|风景|森林|海边|城市|建筑/g)?.map((word, idx) => (
                        <span key={idx} className="inline-block mx-1 px-2 py-0.5 bg-purple-100 text-purple-700 rounded text-xs">
                          {word}
                        </span>
                      )) || <span className="text-gray-400">暂无关键词</span>}
                    </div>
                  )}
                </div>
                
                <div className="space-y-2">
                  <span className="text-sm font-medium text-gray-700">选择风格：</span>
                  <div className="flex flex-wrap gap-2">
                    {styles.map(style => (
                      <button 
                        key={style.name} 
                        onClick={() => setSelectedStyle(selectedStyle === style.name ? '' : style.name)}
                        className={`px-3 py-1 text-xs rounded-full transition-all ${
                          selectedStyle === style.name 
                            ? 'bg-purple-600 text-white' 
                            : 'bg-gray-100 hover:bg-purple-100 text-gray-700'
                        }`}
                      >
                        {style.name}
                      </button>
                    ))}
                  </div>
                                </div>
                
                {/* 快速标签 */}
                <div className="border-t pt-4">
                  <div className="flex justify-between items-center mb-3">
                    <span className="text-sm font-medium text-gray-700">快速插入标签</span>
                    <button
                      onClick={() => setShowQuickTags(!showQuickTags)}
                      className="text-xs text-purple-600 hover:text-purple-800 transition-colors"
                    >
                      {showQuickTags ? '收起' : '展开'}
                    </button>
                  </div>
                  {showQuickTags && (
                    <div className="space-y-3">
                      {quickTags.map((category, idx) => (
                        <div key={idx} className="space-y-2">
                          <span className="text-xs font-medium text-gray-600">{category.category}</span>
                          <div className="flex flex-wrap gap-2">
                            {category.tags.map((tag, tagIdx) => (
                              <button
                                key={tagIdx}
                                onClick={() => insertTag(tag)}
                                className="px-3 py-1.5 text-xs bg-gradient-to-r from-gray-50 to-gray-100 hover:from-purple-50 hover:to-purple-100 text-gray-700 rounded-lg border border-gray-200 hover:border-purple-300 transition-all group"
                                title={`${tag.en} - ${tag.cn}`}
                              >
                                <span className="font-medium text-gray-800">{tag.en}</span>
                                <span className="text-gray-500 ml-1 text-xs group-hover:text-purple-600">({tag.cn})</span>
                              </button>
                            ))}
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
                
                <button
                  onClick={handleGenerate}
                  disabled={!inputText.trim() || isGenerating}
                  className="w-full bg-gradient-to-r from-purple-600 via-purple-700 to-blue-600 text-white py-3 px-6 rounded-lg font-medium hover:shadow-lg hover:scale-105 transition-all disabled:opacity-50 disabled:cursor-not-allowed relative transform"
                >
                  {isGenerating ? (
                    <>
                      <div className="animate-spin inline-block w-4 h-4 border-2 border-white border-t-transparent rounded-full mr-2"></div>
                      生成中...
                    </>
                  ) : (
                    <>
                      <Sparkles className="inline mr-2" size={16} />
                      生成提示词
                    </>
                  )}
                </button>
              </div>
              
              {generatedPrompt && (
                <div className="mt-6 p-4 bg-gradient-to-br from-green-50 to-emerald-50 border border-green-200/50 rounded-lg animate-slide-up shadow-sm">
                  <div className="flex justify-between items-start mb-3">
                    <h3 className="font-medium text-gray-900 flex items-center">
                      <Sparkles className="mr-2 text-green-600" size={16} />
                      生成的提示词
                      <span className="ml-2 text-xs bg-green-100 text-green-700 px-2 py-0.5 rounded-full">已优化</span>
                    </h3>
                    <div className="flex gap-2">
                      <button 
                        onClick={() => handleCopy(generatedPrompt)}
                        className="p-2 hover:bg-green-100 rounded-lg transition-colors relative group"
                        title="复制到剪贴板"
                      >
                        {copyStatus === 'copied' ? (
                          <CheckCircle size={16} className="text-green-600" />
                        ) : copyStatus === 'error' ? (
                          <AlertCircle size={16} className="text-red-600" />
                        ) : (
                          <Copy size={16} className="text-gray-600 group-hover:text-green-600" />
                        )}
                      </button>
                      <button 
                        className="p-2 hover:bg-green-100 rounded-lg transition-colors group"
                        title="收藏"
                      >
                        <Heart size={16} className="text-gray-600 group-hover:text-red-500" />
                      </button>
                    </div>
                  </div>
                  <div className="bg-white p-3 rounded border border-green-200/30">
                    <p className="text-sm text-gray-700 leading-relaxed">{generatedPrompt}</p>
                  </div>
                  <div className="mt-3 flex justify-between items-center text-xs text-gray-600">
                    <span>字符数：{generatedPrompt.length}</span>
                    <span>标签数：{generatedPrompt.split(',').length}</span>
                  </div>
                </div>
              )}
            </div>
            
            {/* 快速提示 */}
            <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-xl p-4 border border-blue-200/50 shadow-sm">
              <h3 className="font-medium text-blue-900 mb-3 flex items-center">
                💡 i-Prompt 使用技巧
                <span className="ml-2 text-xs bg-blue-100 text-blue-700 px-2 py-0.5 rounded-full">Pro Tips</span>
              </h3>
              <ul className="text-sm text-blue-800 space-y-2">
                <li className="flex items-start">
                  <span className="text-blue-500 mr-2">▸</span>
                  详细描述能生成更精确的提示词
                </li>
                <li className="flex items-start">
                  <span className="text-blue-500 mr-2">▸</span>
                  选择合适的风格会自动添加相关标签
                </li>
                <li className="flex items-start">
                  <span className="text-blue-500 mr-2">▸</span>
                  可以多次生成，选择最满意的结果
                </li>
                <li className="flex items-start">
                  <span className="text-purple-500 mr-2">★</span>
                  IP助手会智能优化你的创意描述
                </li>
              </ul>
            </div>
          </div>
        );
        
      case 'library':
        return (
          <div className="space-y-6 animate-fade-in">
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                <Search className="mr-2 text-blue-600" size={20} />
                i-Prompt 词库
              </h2>
              
              <div className="mb-6">
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
                  <input
                    type="text"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    placeholder="搜索提示词、标签或效果..."
                    className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                  />
                </div>
              </div>
              
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-6">
                {promptCategories.map(category => (
                  <div key={category.name} className="p-4 border border-gray-200 rounded-lg hover:shadow-md transition-all cursor-pointer group">
                    <div className="flex justify-between items-center mb-2">
                      <h3 className="font-medium text-gray-900 group-hover:text-blue-600 transition-colors">{category.name}</h3>
                      <span className={`px-2 py-1 text-xs rounded-full ${category.color}`}>
                        {category.count}
                      </span>
                    </div>
                    <p className="text-xs text-gray-500">{category.description}</p>
                  </div>
                ))}
              </div>
              
              <div className="space-y-3">
                <h3 className="font-medium text-gray-900">热门提示词</h3>
                {hotPrompts.map((item, index) => (
                  <div key={index} className="p-3 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors">
                    <div className="flex justify-between items-start mb-1">
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-1">
                          <p className="text-sm font-medium text-gray-900">{item.en}</p>
                          <span className="text-xs px-2 py-0.5 bg-gray-200 text-gray-600 rounded">{item.category}</span>
                        </div>
                        <p className="text-xs text-gray-600">{item.cn}</p>
                      </div>
                      <div className="flex items-center gap-2 ml-4">
                        <span className="text-xs text-gray-500">{item.usage}</span>
                        <button 
                          onClick={() => handleCopy(item.en)}
                          className="p-1 hover:bg-gray-200 rounded transition-colors"
                        >
                          <Copy size={14} className="text-gray-600" />
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        );
        
      case 'extractor':
        return (
          <div className="space-y-6 animate-fade-in">
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                <Image className="mr-2 text-green-600" size={20} />
                图像信息提取器
              </h2>
              
              <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center hover:border-green-400 transition-colors">
                <Upload className="mx-auto mb-4 text-gray-400" size={48} />
                <p className="text-lg font-medium text-gray-900 mb-2">拖拽或点击上传图片</p>
                <p className="text-sm text-gray-600 mb-4">支持 PNG, JPG, WEBP 格式</p>
                <button className="bg-green-600 text-white px-6 py-2 rounded-lg hover:bg-green-700 transition-colors">
                  选择文件
                </button>
              </div>
              
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-6">
                <div className="p-4 bg-blue-50 rounded-lg">
                  <h3 className="font-medium text-blue-900 mb-2">支持格式</h3>
                  <ul className="text-sm text-blue-800 space-y-1">
                    <li>• Stable Diffusion 生成的图片</li>
                    <li>• ComfyUI 工作流图片</li>
                    <li>• Midjourney 图片（部分信息）</li>
                    <li>• NovelAI 生成图片</li>
                  </ul>
                </div>
                <div className="p-4 bg-green-50 rounded-lg">
                  <h3 className="font-medium text-green-900 mb-2">提取信息</h3>
                  <ul className="text-sm text-green-800 space-y-1">
                    <li>• 正向和反向提示词</li>
                    <li>• 生成参数（采样器、步数等）</li>
                    <li>• 模型信息和版本</li>
                    <li>• 种子值和尺寸信息</li>
                  </ul>
                </div>
              </div>
            </div>
          </div>
        );
        
      case 'tools':
        return (
          <div className="space-y-6 animate-fade-in">
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                <Settings className="mr-2 text-orange-600" size={20} />
                辅助工具
              </h2>
              
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {tools.map((tool, index) => (
                  <div key={index} className="p-4 border border-gray-200 rounded-lg hover:shadow-md transition-all cursor-pointer group">
                    <div className="flex items-start">
                      <tool.icon className={`mr-3 mt-1 ${tool.color} group-hover:scale-110 transition-transform`} size={20} />
                      <div>
                        <h3 className="font-medium text-gray-900 mb-1 group-hover:text-gray-700">{tool.name}</h3>
                        <p className="text-sm text-gray-600">{tool.desc}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
              
              {/* 工具预览区域 */}
              <div className="mt-6 p-4 bg-gray-50 rounded-lg">
                <h3 className="font-medium text-gray-900 mb-2">快速操作</h3>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
                  <button className="p-2 bg-white border border-gray-200 rounded text-sm hover:bg-gray-100 transition-colors">
                    权重增强 (word:1.2)
                  </button>
                  <button className="p-2 bg-white border border-gray-200 rounded text-sm hover:bg-gray-100 transition-colors">
                    权重减弱 [word:0.8]
                  </button>
                  <button className="p-2 bg-white border border-gray-200 rounded text-sm hover:bg-gray-100 transition-colors">
                    随机灵感
                  </button>
                </div>
              </div>
            </div>
          </div>
        );
        
      case 'tutorial':
        return (
          <div className="space-y-6 animate-fade-in">
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                <BookOpen className="mr-2 text-indigo-600" size={20} />
                学习教程
              </h2>
              
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {tutorials.map((tutorial, index) => (
                  <div key={index} className="p-4 border border-gray-200 rounded-lg hover:shadow-md transition-all cursor-pointer group">
                    <h3 className="font-medium text-gray-900 mb-2 group-hover:text-indigo-600 transition-colors">{tutorial.title}</h3>
                    <p className="text-sm text-gray-600 mb-3 line-clamp-2">{tutorial.description}</p>
                    <div className="flex justify-between items-center mb-2">
                      <span className={`px-2 py-1 text-xs rounded-full ${
                        tutorial.level === '新手' ? 'bg-green-100 text-green-800' :
                        tutorial.level === '初级' ? 'bg-blue-100 text-blue-800' :
                        tutorial.level === '进阶' ? 'bg-orange-100 text-orange-800' :
                        'bg-red-100 text-red-800'
                      }`}>
                        {tutorial.level}
                      </span>
                      <span className="text-xs text-gray-500">{tutorial.time}</span>
                    </div>
                    {tutorial.progress > 0 && (
                      <div className="w-full bg-gray-200 rounded-full h-2">
                        <div 
                          className="bg-indigo-600 h-2 rounded-full transition-all" 
                          style={{width: `${tutorial.progress}%`}}
                        ></div>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </div>
          </div>
        );
        
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-purple-50 to-indigo-50 flex flex-col">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <div className="flex items-center">
                <div className="w-8 h-8 rounded-lg overflow-hidden flex items-center justify-center">
                  <img src="/logo192.png" alt="i-Prompt" className="w-full h-full object-contain" />
                </div>
                <h1 className="ml-3 text-xl font-bold text-gray-900">i-Prompt</h1>
                <span className="ml-2 px-2 py-0.5 text-xs bg-purple-100 text-purple-600 rounded-full">Beta</span>
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              <button className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100 transition-colors">
                <Heart size={20} />
              </button>
              <button className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100 transition-colors">
                <Download size={20} />
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Navigation */}
      <nav className="bg-white border-b border-gray-200 sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex space-x-8 overflow-x-auto">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex items-center py-4 px-1 border-b-2 font-medium text-sm whitespace-nowrap transition-all ${
                  activeTab === tab.id
                    ? 'border-purple-500 text-purple-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                <tab.icon className="mr-2" size={16} />
                {tab.label}
              </button>
            ))}
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="flex-1 max-w-7xl mx-auto w-full px-4 sm:px-6 lg:px-8 py-8">
        {renderContent()}
      </main>

      {/* Footer */}
      <footer className="bg-white border-t border-gray-200 mt-auto">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex flex-col md:flex-row justify-between items-center">
            <p className="text-sm text-gray-500 mb-4 md:mb-0">© 2025 i-Prompt. 智能提示词助手，让AI创作更简单.</p>
            <div className="flex space-x-6 text-sm text-gray-500">
              <a href="#" className="hover:text-gray-700 transition-colors">帮助中心</a>
              <a href="#" className="hover:text-gray-700 transition-colors">反馈建议</a>
              <a href="#" className="hover:text-gray-700 transition-colors">关于我们</a>
            </div>
          </div>
        </div>
      </footer>
      
      {/* 复制状态提示 */}
      {copyStatus && (
        <div className={`fixed bottom-4 right-4 px-4 py-2 rounded-lg text-white text-sm transition-opacity ${
          copyStatus === 'copied' ? 'bg-green-600' : 'bg-red-600'
        }`}>
          {copyStatus === 'copied' ? '✅ 已复制到剪贴板' : '❌ 复制失败'}
        </div>
      )}
    </div>
  );
};

export default AIPaintingAssistant;
