      // 随机添加质量、光照和构图标签
      const randomQuality = qualityTags[Math.floor(Math.random() * qualityTags.length)];
      const randomLighting = lightingEffects[Math.floor(Math.random() * lightingEffects.length)];
      const randomComposition = compositionTags[Math.floor(Math.random() * compositionTags.length)];
      
      // 标记为本地生成
      return { content: `${enhancedPrompt}, ${randomQuality}, ${randomLighting}, ${randomComposition}`, source: 'local' };
