              >
                {isExtracting ? (
                  <div className="space-y-4">
                    <div className="animate-spin mx-auto w-8 h-8 border-2 border-green-600 border-t-transparent rounded-full"></div>
                    <p className="text-lg font-medium text-gray-900">正在提取图像信息...</p>
                    <p className="text-sm text-gray-600">AI正在分析图像元数据</p>
                  </div>
                ) : uploadedImage ? (
                  <div className="space-y-4">
                    <img src={uploadedImage} alt="Uploaded" className="mx-auto max-w-xs max-h-48 rounded-lg shadow-md" />
                    <p className="text-lg font-medium text-green-700">图像上传成功！</p>
                    <button 
                      onClick={(e) => {
                        e.stopPropagation();
                        setUploadedImage(null);
                        setExtractedData(null);
                      }}
                      className="text-sm text-gray-600 hover:text-gray-800 underline"
                    >
                      重新上传
                    </button>
                  </div>
                ) : (
                  <div className="space-y-4">
                    <Upload className="mx-auto mb-4 text-gray-400" size={48} />
                    <p className="text-lg font-medium text-gray-900">拖拽或点击上传图片</p>
                    <p className="text-sm text-gray-600">支持 PNG, JPG, WEBP 格式</p>
                    <button className="bg-gradient-to-r from-green-600 to-blue-600 text-white px-6 py-2 rounded-lg hover:shadow-lg transition-all">
                      Select File 选择文件
                    </button>
                  </div>
                )}
                
                <input
                  id="file-input"
                  type="file"
                  accept="image/*"
                  onChange={(e) => handleFileUpload(e.target.files[0])}
                  className="hidden"
                />
              </div>
              
              {/* 提取结果 */}
              {extractedData && (
                <div className="mt-6 p-4 bg-gradient-to-br from-green-50 to-blue-50 border border-green-200/50 rounded-lg">
                  <h3 className="font-medium text-gray-900 mb-4 flex items-center">
                    <Sparkles className="mr-2 text-green-600" size={16} />
                    Extracted Information 提取的信息
                  </h3>
                  
                  <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
                    <div className="space-y-3">
                      <div className="bg-white p-3 rounded border">
                        <div className="flex justify-between items-center mb-2">
                          <span className="text-sm font-medium text-gray-700">Positive Prompt 正向提示词</span>
                          <button
                            onClick={() => handleCopy(extractedData.prompt)}
                            className="p-1 hover:bg-gray-100 rounded transition-colors"
                            title="复制提示词"
                          >
                            <Copy size={14} className="text-gray-600" />
                          </button>
                        </div>
                        <p className="text-xs text-gray-700 break-words">{extractedData.prompt}</p>
                      </div>
                      
                      <div className="bg-white p-3 rounded border">
                        <div className="flex justify-between items-center mb-2">
                          <span className="text-sm font-medium text-gray-700">Negative Prompt 反向提示词</span>
                          <button
                            onClick={() => handleCopy(extractedData.negativePrompt)}
                            className="p-1 hover:bg-gray-100 rounded transition-colors"
                            title="复制反向提示词"
                          >
                            <Copy size={14} className="text-gray-600" />
                          </button>
                        </div>
                        <p className="text-xs text-gray-700 break-words">{extractedData.negativePrompt}</p>
                      </div>
                    </div>
                    
                    <div className="grid grid-cols-2 gap-3">
                      <div className="bg-white p-3 rounded border">
                        <span className="text-sm font-medium text-gray-700">Model 模型</span>
                        <p className="text-xs text-gray-600 mt-1">{extractedData.model}</p>
                      </div>
                      <div className="bg-white p-3 rounded border">
                        <span className="text-sm font-medium text-gray-700">Sampler 采样器</span>
                        <p className="text-xs text-gray-600 mt-1">{extractedData.sampler}</p>
                      </div>
                      <div className="bg-white p-3 rounded border">
                        <span className="text-sm font-medium text-gray-700">Steps 步数</span>
                        <p className="text-xs text-gray-600 mt-1">{extractedData.steps}</p>
                      </div>
                      <div className="bg-white p-3 rounded border">
                        <span className="text-sm font-medium text-gray-700">CFG Scale</span>
                        <p className="text-xs text-gray-600 mt-1">{extractedData.cfgScale}</p>
                      </div>
                      <div className="bg-white p-3 rounded border">
                        <span className="text-sm font-medium text-gray-700">Seed 种子</span>
                        <p className="text-xs text-gray-600 mt-1">{extractedData.seed}</p>
                      </div>
                      <div className="bg-white p-3 rounded border">
                        <span className="text-sm font-medium text-gray-700">Size 尺寸</span>
                        <p className="text-xs text-gray-600 mt-1">{extractedData.size}</p>
                      </div>
                    </div>
                  </div>
                </div>
              )}
              
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-6">
                <div className="p-4 bg-blue-50 rounded-lg">
                  <h3 className="font-medium text-blue-900 mb-2">支持格式</h3>
                  <ul className="text-sm text-blue-800 space-y-1">
                    <li>• Stable Diffusion 生成的图片</li>
                    <li>• ComfyUI 工作流图片</li>
                    <li>• Midjourney 图片（部分信息）</li>
                    <li>• NovelAI 生成图片</li>
                  </ul>
                </div>
                <div className="p-4 bg-green-50 rounded-lg">
                  <h3 className="font-medium text-green-900 mb-2">提取信息</h3>
                  <ul className="text-sm text-green-800 space-y-1">
                    <li>• 正向和反向提示词</li>
                    <li>• 生成参数（采样器、步数等）</li>
                    <li>• 模型信息和版本</li>
                    <li>• 种子值和尺寸信息</li>
                  </ul>
                </div>
              </div>
            </div>
          </div>
        );
        
      case 'tools':
        return (
          <div className="space-y-6 animate-fade-in">
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                <Settings className="mr-2 text-orange-600" size={20} />
                辅助工具
              </h2>
              
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {tools.map((tool, index) => (
                  <div key={index} className="p-4 border border-gray-200 rounded-lg hover:shadow-md transition-all cursor-pointer group">
                    <div className="flex items-start">
                      <tool.icon className={`mr-3 mt-1 ${tool.color} group-hover:scale-110 transition-transform`} size={20} />
                      <div>
                        <h3 className="font-medium text-gray-900 mb-1 group-hover:text-gray-700">{tool.name}</h3>
                        <p className="text-sm text-gray-600">{tool.desc}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
              
              {/* 工具预览区域 */}
              <div className="mt-6 p-4 bg-gray-50 rounded-lg">
                <h3 className="font-medium text-gray-900 mb-2">快速操作</h3>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
                  <button className="p-2 bg-white border border-gray-200 rounded text-sm hover:bg-gray-100 transition-colors">
                    权重增强 (word:1.2)
                  </button>
                  <button className="p-2 bg-white border border-gray-200 rounded text-sm hover:bg-gray-100 transition-colors">
                    权重减弱 [word:0.8]
                  </button>
                  <button className="p-2 bg-white border border-gray-200 rounded text-sm hover:bg-gray-100 transition-colors">
                    随机灵感
                  </button>
                </div>
              </div>
            </div>
          </div>
        );
        
      case 'tutorial':
        return (
          <div className="space-y-6 animate-fade-in">
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                <BookOpen className="mr-2 text-indigo-600" size={20} />
                学习教程
              </h2>
              
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {tutorials.map((tutorial, index) => (
                  <div key={index} className="p-4 border border-gray-200 rounded-lg hover:shadow-md transition-all cursor-pointer group">
                    <h3 className="font-medium text-gray-900 mb-2 group-hover:text-indigo-600 transition-colors">{tutorial.title}</h3>
                    <p className="text-sm text-gray-600 mb-3 line-clamp-2">{tutorial.description}</p>
                    <div className="flex justify-between items-center mb-2">
                      <span className={`px-2 py-1 text-xs rounded-full ${
                        tutorial.level === '新手' ? 'bg-green-100 text-green-800' :
                        tutorial.level === '初级' ? 'bg-blue-100 text-blue-800' :
                        tutorial.level === '进阶' ? 'bg-orange-100 text-orange-800' :
                        'bg-red-100 text-red-800'
                      }`}>
                        {tutorial.level}
                      </span>
                      <span className="text-xs text-gray-500">{tutorial.time}</span>
                    </div>
                    {tutorial.progress > 0 && (
                      <div className="w-full bg-gray-200 rounded-full h-2">
                        <div 
                          className="bg-indigo-600 h-2 rounded-full transition-all" 
                          style={{width: `${tutorial.progress}%`}}
                        ></div>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </div>
          </div>
        );
        
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-purple-50 to-indigo-50 flex flex-col">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <div className="flex items-center">
                <div className="w-8 h-8 rounded-lg overflow-hidden flex items-center justify-center">
                  <img src="/logo192.png" alt="i-Prompt" className="w-full h-full object-contain" />
                </div>
                <h1 className="ml-3 text-xl font-bold text-gray-900">i-Prompt</h1>
                <span className="ml-2 px-2 py-0.5 text-xs bg-purple-100 text-purple-600 rounded-full">Beta</span>
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              <button className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100 transition-colors">
                <Heart size={20} />
              </button>
              <button className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100 transition-colors">
                <Download size={20} />
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Navigation */}
      <nav className="bg-white border-b border-gray-200 sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex space-x-8 overflow-x-auto">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex items-center py-4 px-1 border-b-2 font-medium text-sm whitespace-nowrap transition-all ${
                  activeTab === tab.id
                    ? 'border-purple-500 text-purple-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                <tab.icon className="mr-2" size={16} />
                {tab.label}
              </button>
            ))}
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="flex-1 max-w-7xl mx-auto w-full px-4 sm:px-6 lg:px-8 py-8">
        {renderContent()}
      </main>

      {/* Footer */}
      <footer className="bg-white border-t border-gray-200 mt-auto">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex flex-col md:flex-row justify-between items-center">
            <p className="text-sm text-gray-500 mb-4 md:mb-0">© 2025 i-Prompt. 智能提示词助手，让AI创作更简单.</p>
            <div className="flex space-x-6 text-sm text-gray-500">
              <a href="#" className="hover:text-gray-700 transition-colors">帮助中心</a>
              <a href="#" className="hover:text-gray-700 transition-colors">反馈建议</a>
              <a href="#" className="hover:text-gray-700 transition-colors">关于我们</a>
            </div>
          </div>
        </div>
      </footer>
      
      {/* 复制状态提示 */}
      {copyStatus && (
        <div className={`fixed bottom-4 right-4 px-4 py-2 rounded-lg text-white text-sm transition-opacity ${
          copyStatus === 'copied' ? 'bg-green-600' : 'bg-red-600'
        }`}>
          {copyStatus === 'copied' ? '✅ 已复制到剪贴板' : '❌ 复制失败'}
        </div>
      )}
    </div>
  );
};

export default AIPaintingAssistant;
