/**
 * 标签胶囊组件 - 重新设计
 */
const TagPill = ({ 
  tag, 
  index,
  isDisabled,
  onDelete, 
  onToggleDisabled,
  onAdjustWeight,
  onAdjustBrackets,
  onCopy,
  onFavorite,
  onTranslate,
  translatedTags,
  hoveredTag,
  setHoveredTag
}) => {
  const [translation, setTranslation] = useState('');
  const [isTranslating, setIsTranslating] = useState(false);

  // 解析标签
  const parseTag = (tag) => {
    // 检测权重 (tag:1.2)
    const weightMatch = tag.match(/^(.+?):([0-9.]+)$/);
    if (weightMatch) {
      return {
        text: weightMatch[1],
        weight: parseFloat(weightMatch[2]),
        bracketType: 'none',
        brackets: 0
      };
    }

    // 检测括号类型和层级
    let brackets = 0;
    let text = tag;
    let bracketType = 'none';

    // 检测圆括号 ()
    if (text.startsWith('(') && text.endsWith(')')) {
      bracketType = 'round';
      while (text.startsWith('(') && text.endsWith(')')) {
        brackets++;
        text = text.slice(1, -1);
      }
    }
    // 检测花括号 {}
    else if (text.startsWith('{') && text.endsWith('}')) {
      bracketType = 'curly';
      while (text.startsWith('{') && text.endsWith('}')) {
        brackets++;
        text = text.slice(1, -1);
      }
    }
    // 检测方括号 []
    else if (text.startsWith('[') && text.endsWith(']')) {
      bracketType = 'square';
      while (text.startsWith('[') && text.endsWith(']')) {
        brackets++;
        text = text.slice(1, -1);
      }
    }

    return { text, weight: 1.0, bracketType, brackets };
  };

  const parsed = parseTag(tag);
  const isHovered = hoveredTag === index;

  // 加载翻译
  useEffect(() => {
    if (parsed.text && !translatedTags[parsed.text]) {
      setIsTranslating(true);
      onTranslate(tag).then(result => {
        // 确保只设置字符串，不是对象
        const translatedText = typeof result === 'object' && result.translatedText ? result.translatedText : result;
        setTranslation(translatedText);
        setIsTranslating(false);
      }).catch(error => {
        console.error('翻译失败:', error);
        setTranslation(parsed.text); // 翻译失败时显示原文
        setIsTranslating(false);
      });
    } else if (translatedTags[parsed.text]) {
      setTranslation(translatedTags[parsed.text]);
    }
  }, [tag, translatedTags, onTranslate, parsed.text]);

  return (
    <div 
      className="relative group"
      onMouseEnter={() => setHoveredTag(index)}
      onMouseLeave={(e) => {
        // 扩大判定范围：检查鼠标是否真的离开了整个区域
        const rect = e.currentTarget.getBoundingClientRect();
        const x = e.clientX;
        const y = e.clientY;

        // 扩大检测范围20像素
        const margin = 20;
        if (x < rect.left - margin || x > rect.right + margin || 
            y < rect.top - margin || y > rect.bottom + 140) { // 底部多留140px给编辑栏
          setHoveredTag(null);
        }
      }}
    >
      {/* 主标签胶囊 */}
      <div
        className={`inline-flex flex-col gap-1 px-4 py-3 rounded-lg text-sm font-medium transition-all duration-200 cursor-pointer min-w-0 ${
          isDisabled 
            ? 'bg-gray-300 text-gray-500 opacity-50' 
            : 'bg-blue-100 text-blue-800 hover:bg-blue-200'
        }`}
      >
        <div className="flex items-center gap-2">
          <span className={`break-all ${isDisabled ? 'line-through' : ''}`}>{tag}</span>
          <button
            onClick={onDelete}
            className="flex-shrink-0 w-4 h-4 rounded-full bg-blue-600 text-white flex items-center justify-center hover:bg-red-500 transition-colors"
          >
            <X size={10} />
          </button>
        </div>

        {/* 中文翻译 */}
        {translation && (
          <div className="text-xs text-gray-600 px-1">
            {isTranslating ? '翻译中...' : translation}
          </div>
        )}
      </div>

      {/* 悬停编辑栏 */}
      {isHovered && (
        <div 
          className="absolute top-full left-0 mt-3 bg-white border-2 border-gray-300 rounded-xl shadow-xl p-4 z-30 min-w-max"
          onMouseEnter={() => setHoveredTag(index)}
          onMouseLeave={() => setHoveredTag(null)}
        >
          <div className="flex flex-col gap-4">
            {/* 第一行：权重调整 */}
            <div className="flex items-center gap-2">
              <span className="text-sm text-gray-700 min-w-12 font-medium">权重:</span>
              <button
                onClick={() => onAdjustWeight(-0.1)}
                className="w-8 h-8 rounded-lg bg-gray-100 hover:bg-gray-200 flex items-center justify-center font-bold text-gray-600"
              >
                -
              </button>
              <div className="text-sm font-mono min-w-12 text-center bg-gray-50 px-3 py-1 rounded-lg border">
                {parsed.weight.toFixed(1)}
              </div>
              <button
                onClick={() => onAdjustWeight(0.1)}
                className="w-8 h-8 rounded-lg bg-gray-100 hover:bg-gray-200 flex items-center justify-center font-bold text-gray-600"
              >
                +
              </button>
            </div>

            {/* 第二行：括号类型控制 */}
            <div className="flex items-center gap-3">
              <span className="text-sm text-gray-700 min-w-12 font-medium">括号:</span>

              {/* 圆括号 () - 1.1倍权重 */}
              <div className="flex items-center gap-1">
                <button
                  onClick={() => onAdjustBrackets('round', -1)}
                  className={`w-7 h-7 rounded-lg text-xs flex items-center justify-center font-bold ${
                    parsed.bracketType === 'round' 
                      ? 'bg-orange-500 hover:bg-orange-600 text-white' 
                      : 'bg-gray-100 hover:bg-gray-200 text-gray-400'
                  }`}
                  disabled={parsed.bracketType !== 'none' && parsed.bracketType !== 'round'}
                >
                  -
                </button>
                <div className={`px-3 py-1 rounded-lg text-sm font-mono border-2 ${
                  parsed.bracketType === 'round' 
                    ? 'bg-orange-100 text-orange-800 border-orange-300' 
                    : 'bg-gray-100 text-gray-500 border-gray-300'
                }`}>
                  () {parsed.bracketType === 'round' ? parsed.brackets : 0}
                </div>
                <button
                  onClick={() => onAdjustBrackets('round', 1)}
                  className={`w-7 h-7 rounded-lg text-xs flex items-center justify-center font-bold ${
                    (parsed.bracketType === 'none' || parsed.bracketType === 'round') && parsed.brackets < 5
                      ? 'bg-orange-500 hover:bg-orange-600 text-white' 
                      : 'bg-gray-100 text-gray-400'
                  }`}
                  disabled={parsed.bracketType !== 'none' && parsed.bracketType !== 'round'}
                >
                  +
                </button>
              </div>

              {/* 花括号 {} - 1.05倍权重 */}
              <div className="flex items-center gap-1">
                <button
                  onClick={() => onAdjustBrackets('curly', -1)}
                  className={`w-7 h-7 rounded-lg text-xs flex items-center justify-center font-bold ${
                    parsed.bracketType === 'curly' 
                      ? 'bg-yellow-500 hover:bg-yellow-600 text-white' 
                      : 'bg-gray-100 hover:bg-gray-200 text-gray-400'
                  }`}
                  disabled={parsed.bracketType !== 'none' && parsed.bracketType !== 'curly'}
                >
                  -
                </button>
                <div className={`px-3 py-1 rounded-lg text-sm font-mono border-2 ${
                  parsed.bracketType === 'curly' 
                    ? 'bg-yellow-100 text-yellow-800 border-yellow-300' 
                    : 'bg-gray-100 text-gray-500 border-gray-300'
                }`}>
                  {`{}`} {parsed.bracketType === 'curly' ? parsed.brackets : 0}
                </div>
                <button
                  onClick={() => onAdjustBrackets('curly', 1)}
                  className={`w-7 h-7 rounded-lg text-xs flex items-center justify-center font-bold ${
                    (parsed.bracketType === 'none' || parsed.bracketType === 'curly') && parsed.brackets < 5
                      ? 'bg-yellow-500 hover:bg-yellow-600 text-white' 
                      : 'bg-gray-100 text-gray-400'
                  }`}
                  disabled={parsed.bracketType !== 'none' && parsed.bracketType !== 'curly'}
                >
                  +
                </button>
              </div>

              {/* 方括号 [] - 0.9倍权重 */}
              <div className="flex items-center gap-1">
                <button
                  onClick={() => onAdjustBrackets('square', -1)}
                  className={`w-7 h-7 rounded-lg text-xs flex items-center justify-center font-bold ${
                    parsed.bracketType === 'square' 
                      ? 'bg-blue-500 hover:bg-blue-600 text-white' 
                      : 'bg-gray-100 hover:bg-gray-200 text-gray-400'
                  }`}
                  disabled={parsed.bracketType !== 'none' && parsed.bracketType !== 'square'}
                >
                  -
                </button>
                <div className={`px-3 py-1 rounded-lg text-sm font-mono border-2 ${
                  parsed.bracketType === 'square' 
                    ? 'bg-blue-100 text-blue-800 border-blue-300' 
                    : 'bg-gray-100 text-gray-500 border-gray-300'
                }`}>
                  [] {parsed.bracketType === 'square' ? parsed.brackets : 0}
                </div>
                <button
                  onClick={() => onAdjustBrackets('square', 1)}
                  className={`w-7 h-7 rounded-lg text-xs flex items-center justify-center font-bold ${
                    (parsed.bracketType === 'none' || parsed.bracketType === 'square') && parsed.brackets < 5
                      ? 'bg-blue-500 hover:bg-blue-600 text-white' 
                      : 'bg-gray-100 text-gray-400'
                  }`}
                  disabled={parsed.bracketType !== 'none' && parsed.bracketType !== 'square'}
                >
                  +
                </button>
              </div>
            </div>

            {/* 第三行：功能按钮 */}
            <div className="flex items-center gap-2">
              <button
                onClick={onCopy}
                className="flex items-center gap-1 px-3 py-2 rounded-lg bg-green-100 hover:bg-green-200 text-green-700 text-sm transition-colors"
                title="复制"
              >
                <Copy size={14} />
                复制
              </button>
              <button
                onClick={onFavorite}
                className="flex items-center gap-1 px-3 py-2 rounded-lg bg-red-100 hover:bg-red-200 text-red-700 text-sm transition-colors"
                title="收藏"
              >
                <Heart size={14} />
                收藏
              </button>
              <button
                onClick={onToggleDisabled}
                className={`flex items-center gap-1 px-3 py-2 rounded-lg text-sm transition-colors ${
                  isDisabled 
                    ? 'bg-blue-100 hover:bg-blue-200 text-blue-700' 
                    : 'bg-gray-100 hover:bg-gray-200 text-gray-700'
                }`}
                title={isDisabled ? "启用" : "禁用"}
              >
                {isDisabled ? <Eye size={14} /> : <EyeOff size={14} />}
                {isDisabled ? "启用" : "禁用"}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

/**
 * 标签卡片组件
 */
const TagCard = ({ tag, onAdd, onToggleFavorite, isFavorited }) => (
  <div className="group bg-gradient-to-r from-gray-50 to-white border border-gray-200 rounded-lg p-4 hover:shadow-md hover:border-blue-300 transition-all duration-200 cursor-pointer">
    <div onClick={onAdd} className="flex-1">
      <div className="flex items-start justify-between mb-2">
        <div className="flex-1">
          <h4 className="font-medium text-gray-900 text-sm mb-1 group-hover:text-blue-700 transition-colors">
            {tag.en}
          </h4>
          <p className="text-xs text-blue-600 mb-2">
            {tag.cn}
          </p>
        </div>
        <button
          onClick={(e) => {
            e.stopPropagation();
            onToggleFavorite();
          }}
          className={`p-1 rounded-full transition-colors ${
            isFavorited 
              ? 'text-red-500 hover:bg-red-50' 
              : 'text-gray-400 hover:bg-gray-100 hover:text-red-500'
          }`}
        >
          <Heart size={14} fill={isFavorited ? 'currentColor' : 'none'} />
        </button>
      </div>

      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <div className="flex items-center gap-1">
            <TrendingUp size={12} className="text-green-600" />
            <span className="text-xs text-green-600 font-medium">{tag.frequency}%</span>
          </div>
          <div className="w-12 bg-gray-200 rounded-full h-1">
            <div 
              className="bg-green-500 h-1 rounded-full transition-all" 
              style={{ width: `${tag.frequency}%` }}
            />
          </div>
        </div>

        <button
          onClick={onAdd}
          className="opacity-0 group-hover:opacity-100 transition-opacity text-xs bg-blue-600 text-white px-2 py-1 rounded-full hover:bg-blue-700"
        >
          <Plus size={10} className="inline mr-1" />
          添加
        </button>
      </div>
    </div>
  </div>
);

export default PromptLibraryPage;
