import React, { useState, useMemo } from 'react';
import { Search, Heart, Copy, CheckCircle, Filter, TrendingUp, Star, Edit3, Plus, Settings } from 'lucide-react';
import { HOT_PROMPTS, PROMPT_CATEGORIES } from '../constants/data';
import { copyToClipboard } from '../utils/clipboard';
import AdvancedPromptEditor from '../components/AdvancedPromptEditor';

const PromptLibraryPage = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [copyStatus, setCopyStatus] = useState('');
  const [favorites, setFavorites] = useState([]);
  const [showEditor, setShowEditor] = useState(false);
  const [editingPrompt, setEditingPrompt] = useState('');
  const [customPrompts, setCustomPrompts] = useState([]);

  // 筛选提示词
  const filteredPrompts = useMemo(() => {
    let filtered = [...HOT_PROMPTS, ...customPrompts];

    // 搜索过滤
    if (searchQuery.trim()) {
      filtered = filtered.filter(prompt => 
        prompt.en.toLowerCase().includes(searchQuery.toLowerCase()) ||
        prompt.cn.includes(searchQuery) ||
        prompt.category.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // 分类过滤
    if (selectedCategory !== 'all') {
      filtered = filtered.filter(prompt => 
        prompt.category.toLowerCase() === selectedCategory.toLowerCase()
      );
    }

    return filtered;
  }, [searchQuery, selectedCategory, customPrompts]);

  const handleCopy = async (text) => {
    const success = await copyToClipboard(text);
    setCopyStatus(success ? 'copied' : 'error');
    setTimeout(() => setCopyStatus(''), 2000);
  };

  const toggleFavorite = (promptEn) => {
    setFavorites(prev => 
      prev.includes(promptEn) 
        ? prev.filter(fav => fav !== promptEn)
        : [...prev, promptEn]
    );
  };

  // 打开编辑器
  const openEditor = (prompt = '') => {
    setEditingPrompt(prompt);
    setShowEditor(true);
  };

  // 保存自定义提示词
  const saveCustomPrompt = (prompt) => {
    if (prompt.trim()) {
      const newPrompt = {
        en: prompt.trim(),
        cn: '自定义提示词',
        usage: '0%',
        category: 'Custom',
        popularity: 0,
        isCustom: true,
        id: Date.now()
      };
      
      setCustomPrompts(prev => [newPrompt, ...prev]);
      setShowEditor(false);
      setEditingPrompt('');
    }
  };

  // 删除自定义提示词
  const deleteCustomPrompt = (promptId) => {
    setCustomPrompts(prev => prev.filter(p => p.id !== promptId));
  };

  return (
    <div className="space-y-6">
      {/* 页面标题 */}
      <div className="text-center mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2 flex items-center justify-center">
          <Search className="mr-3 text-blue-600" size={32} />
          智能提示词库 2.0
        </h1>
        <p className="text-gray-600 max-w-2xl mx-auto">
          精选优质提示词库，支持智能编辑、一键翻译、权重调节等高级功能，助您快速找到完美的创作灵感
        </p>
      </div>

      {/* 快速操作栏 */}
      <div className="bg-gradient-to-r from-blue-50 to-purple-50 rounded-xl p-4 border border-blue-100">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4">
            <div className="flex items-center gap-2">
              <Edit3 className="text-blue-600" size={20} />
              <span className="font-medium text-gray-900">快速操作</span>
            </div>
            <div className="flex gap-2">
              <button
                onClick={() => openEditor()}
                className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
              >
                <Plus size={16} />
                创建提示词
              </button>
              <button
                onClick={() => openEditor('masterpiece, best quality, ultra detailed, ')}
                className="flex items-center gap-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
              >
                <Edit3 size={16} />
                快速编辑
              </button>
            </div>
          </div>
          <div className="text-sm text-gray-600">
            <span className="font-medium">提示：</span>
            选中任意提示词可进行高级编辑，支持智能补全和权重调节
          </div>
        </div>
      </div>

      {/* 搜索和筛选区域 */}
      <div className="bg-white/80 backdrop-blur-sm rounded-xl shadow-lg border border-blue-100/50 p-6">
        <div className="space-y-4">
          {/* 搜索框 */}
          <div className="relative">
            <Search className="absolute left-3 top-3 text-gray-400" size={20} />
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="搜索提示词... (支持中英文)"
              className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
            />
          </div>

          {/* 分类筛选 */}
          <div className="space-y-3">
            <div className="flex items-center gap-2">
              <Filter size={16} className="text-gray-600" />
              <span className="text-sm font-medium text-gray-700">按类别筛选：</span>
            </div>
            <div className="flex flex-wrap gap-2">
              <button
                onClick={() => setSelectedCategory('all')}
                className={`px-4 py-2 text-sm rounded-lg transition-all ${
                  selectedCategory === 'all'
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-100 hover:bg-blue-100 text-gray-700'
                }`}
              >
                全部 ({filteredPrompts.length})
              </button>
              {PROMPT_CATEGORIES.map(category => (
                <button
                  key={category.name}
                  onClick={() => setSelectedCategory(category.name.split(' ')[0])}
                  className={`px-4 py-2 text-sm rounded-lg transition-all ${
                    selectedCategory === category.name.split(' ')[0]
                      ? 'bg-blue-600 text-white'
                      : `${category.color} hover:opacity-80`
                  }`}
                >
                  {category.name} ({category.count})
                </button>
              ))}
              {customPrompts.length > 0 && (
                <button
                  onClick={() => setSelectedCategory('custom')}
                  className={`px-4 py-2 text-sm rounded-lg transition-all ${
                    selectedCategory === 'custom'
                      ? 'bg-blue-600 text-white'
                      : 'bg-purple-100 text-purple-800 hover:opacity-80'
                  }`}
                >
                  自定义 ({customPrompts.length})
                </button>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* 统计信息 */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <StatCard
          icon={<Search className="text-blue-600" size={24} />}
          title="搜索结果"
          value={filteredPrompts.length}
          subtitle="个提示词"
        />
        <StatCard
          icon={<TrendingUp className="text-green-600" size={24} />}
          title="热门标签"
          value={filteredPrompts.filter(p => p.popularity > 80).length}
          subtitle="高热度"
        />
        <StatCard
          icon={<Star className="text-yellow-600" size={24} />}
          title="我的收藏"
          value={favorites.length}
          subtitle="已收藏"
        />
        <StatCard
          icon={<Edit3 className="text-purple-600" size={24} />}
          title="自定义提示词"
          value={customPrompts.length}
          subtitle="已创建"
        />
      </div>

      {/* 高级编辑器 */}
      {showEditor && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="w-full max-w-4xl max-h-[90vh] overflow-y-auto">
            <AdvancedPromptEditor
              initialPrompt={editingPrompt}
              onSave={saveCustomPrompt}
              onClose={() => {
                setShowEditor(false);
                setEditingPrompt('');
              }}
              className="w-full"
            />
          </div>
        </div>
      )}

      {/* 提示词列表 */}
      <div className="space-y-4">
        {filteredPrompts.length === 0 ? (
          <div className="text-center py-12 bg-white/80 backdrop-blur-sm rounded-xl border border-gray-200">
            <Search className="mx-auto text-gray-400 mb-4" size={48} />
            <p className="text-gray-500 mb-4">未找到匹配的提示词，请尝试其他搜索条件</p>
            <button
              onClick={() => openEditor()}
              className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
            >
              创建新的提示词
            </button>
          </div>
        ) : (
          filteredPrompts.map((prompt, index) => (
            <PromptCard
              key={prompt.id || index}
              prompt={prompt}
              isFavorite={favorites.includes(prompt.en)}
              onCopy={handleCopy}
              onToggleFavorite={() => toggleFavorite(prompt.en)}
              onEdit={() => openEditor(prompt.en)}
              onDelete={prompt.isCustom ? () => deleteCustomPrompt(prompt.id) : null}
              copyStatus={copyStatus}
            />
          ))
        )}
      </div>

      {/* 复制状态提示 */}
      {copyStatus && (
        <div className={`fixed bottom-4 right-4 px-4 py-2 rounded-lg text-white text-sm transition-all duration-300 shadow-lg ${
          copyStatus === 'copied' ? 'bg-green-600' : 'bg-red-600'
        }`}>
          {copyStatus === 'copied' ? '✅ 已复制到剪贴板' : '❌ 复制失败'}
        </div>
      )}
    </div>
  );
};

/**
 * 统计卡片组件
 */
const StatCard = ({ icon, title, value, subtitle }) => (
  <div className="bg-white/80 backdrop-blur-sm rounded-xl p-4 border border-gray-200 shadow-sm hover:shadow-md transition-shadow">
    <div className="flex items-center justify-between">
      <div>
        <p className="text-sm text-gray-600">{title}</p>
        <p className="text-2xl font-bold text-gray-900">{value}</p>
        <p className="text-xs text-gray-500">{subtitle}</p>
      </div>
      <div className="opacity-80">{icon}</div>
    </div>
  </div>
);

/**
 * 提示词卡片组件
 */
const PromptCard = ({ prompt, isFavorite, onCopy, onToggleFavorite, onEdit, onDelete, copyStatus }) => (
  <div className="bg-white/80 backdrop-blur-sm rounded-xl p-6 border border-gray-200 shadow-sm hover:shadow-md transition-all group">
    <div className="flex justify-between items-start mb-4">
      <div className="flex-1">
        <div className="flex items-center gap-3 mb-2">
          <span className={`px-3 py-1 text-xs rounded-full ${
            prompt.category === 'Quality' ? 'bg-purple-100 text-purple-700' :
            prompt.category === 'Character' ? 'bg-blue-100 text-blue-700' :
            prompt.category === 'Effects' ? 'bg-orange-100 text-orange-700' :
            prompt.category === 'Scene' ? 'bg-green-100 text-green-700' :
            prompt.category === 'Style' ? 'bg-pink-100 text-pink-700' :
            prompt.category === 'Custom' ? 'bg-purple-100 text-purple-700' :
            'bg-gray-100 text-gray-700'
          }`}>
            {prompt.category === 'Custom' ? '自定义' : prompt.category}
          </span>
          {prompt.isCustom && (
            <span className="px-2 py-1 text-xs bg-blue-100 text-blue-600 rounded-full">
              可编辑
            </span>
          )}
          <div className="flex items-center gap-1">
            <TrendingUp size={14} className="text-green-600" />
            <span className="text-sm font-medium text-green-600">{prompt.usage}</span>
          </div>
          <div className="flex items-center gap-1">
            <div className="w-16 bg-gray-200 rounded-full h-2">
              <div 
                className="bg-orange-500 h-2 rounded-full" 
                style={{ width: `${prompt.popularity}%` }}
              />
            </div>
            <span className="text-xs text-gray-500">{prompt.popularity}%</span>
          </div>
        </div>
      </div>
      <div className="flex gap-2">
        <button
          onClick={onEdit}
          className="p-2 bg-blue-100 text-blue-600 rounded-lg hover:bg-blue-200 transition-colors opacity-0 group-hover:opacity-100"
          title="编辑提示词"
        >
          <Edit3 size={16} />
        </button>
        <button
          onClick={onToggleFavorite}
          className={`p-2 rounded-lg transition-colors ${
            isFavorite 
              ? 'bg-red-100 text-red-600 hover:bg-red-200' 
              : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
          }`}
          title={isFavorite ? '取消收藏' : '添加收藏'}
        >
          <Heart size={16} fill={isFavorite ? 'currentColor' : 'none'} />
        </button>
        <button
          onClick={() => onCopy(prompt.en)}
          className="p-2 bg-green-100 text-green-600 rounded-lg hover:bg-green-200 transition-colors"
          title="复制提示词"
        >
          {copyStatus === 'copied' ? (
            <CheckCircle size={16} />
          ) : (
            <Copy size={16} />
          )}
        </button>
        {onDelete && (
          <button
            onClick={onDelete}
            className="p-2 bg-red-100 text-red-600 rounded-lg hover:bg-red-200 transition-colors opacity-0 group-hover:opacity-100"
            title="删除自定义提示词"
          >
            <Settings size={16} />
          </button>
        )}
      </div>
    </div>

    <div className="space-y-3">
      {/* 英文提示词 */}
      <div className="bg-gray-50 p-4 rounded-lg cursor-pointer hover:bg-gray-100 transition-colors" onClick={onEdit}>
        <p className="text-sm font-mono text-gray-800 leading-relaxed">{prompt.en}</p>
      </div>
      
      {/* 中文翻译 */}
      <div className="bg-blue-50 p-3 rounded-lg">
        <p className="text-sm text-blue-800">
          <span className="font-medium">中文释义：</span>{prompt.cn}
        </p>
      </div>
    </div>
  </div>
);

export default PromptLibraryPage;
