import React, { useState, useMemo } from 'react';
import { Search, Heart, Copy, CheckCircle, Filter, TrendingUp, Star } from 'lucide-react';
import { HOT_PROMPTS, PROMPT_CATEGORIES } from '../constants/data';
import { copyToClipboard } from '../utils/clipboard';

const PromptLibraryPage = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [copyStatus, setCopyStatus] = useState('');
  const [favorites, setFavorites] = useState([]);

  // 筛选提示词
  const filteredPrompts = useMemo(() => {
    let filtered = HOT_PROMPTS;

    // 搜索过滤
    if (searchQuery.trim()) {
      filtered = filtered.filter(prompt => 
        prompt.en.toLowerCase().includes(searchQuery.toLowerCase()) ||
        prompt.cn.includes(searchQuery) ||
        prompt.category.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // 分类过滤
    if (selectedCategory !== 'all') {
      filtered = filtered.filter(prompt => 
        prompt.category.toLowerCase() === selectedCategory.toLowerCase()
      );
    }

    return filtered;
  }, [searchQuery, selectedCategory]);

  const handleCopy = async (text) => {
    const success = await copyToClipboard(text);
    setCopyStatus(success ? 'copied' : 'error');
    setTimeout(() => setCopyStatus(''), 2000);
  };

  const toggleFavorite = (promptEn) => {
    setFavorites(prev => 
      prev.includes(promptEn) 
        ? prev.filter(fav => fav !== promptEn)
        : [...prev, promptEn]
    );
  };

  return (
    <div className="space-y-6">
      {/* 页面标题 */}
      <div className="text-center mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2 flex items-center justify-center">
          <Search className="mr-3 text-blue-600" size={32} />
          提示词库
        </h1>
        <p className="text-gray-600 max-w-2xl mx-auto">
          精选优质提示词库，包含各种风格和类别的专业标签，助您快速找到完美的创作灵感
        </p>
      </div>

      {/* 搜索和筛选区域 */}
      <div className="bg-white/80 backdrop-blur-sm rounded-xl shadow-lg border border-blue-100/50 p-6">
        <div className="space-y-4">
          {/* 搜索框 */}
          <div className="relative">
            <Search className="absolute left-3 top-3 text-gray-400" size={20} />
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="搜索提示词... (支持中英文)"
              className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
            />
          </div>

          {/* 分类筛选 */}
          <div className="space-y-3">
            <div className="flex items-center gap-2">
              <Filter size={16} className="text-gray-600" />
              <span className="text-sm font-medium text-gray-700">按类别筛选：</span>
            </div>
            <div className="flex flex-wrap gap-2">
              <button
                onClick={() => setSelectedCategory('all')}
                className={`px-4 py-2 text-sm rounded-lg transition-all ${
                  selectedCategory === 'all'
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-100 hover:bg-blue-100 text-gray-700'
                }`}
              >
                全部 ({HOT_PROMPTS.length})
              </button>
              {PROMPT_CATEGORIES.map(category => (
                <button
                  key={category.name}
                  onClick={() => setSelectedCategory(category.name.split(' ')[0])}
                  className={`px-4 py-2 text-sm rounded-lg transition-all ${
                    selectedCategory === category.name.split(' ')[0]
                      ? 'bg-blue-600 text-white'
                      : `${category.color} hover:opacity-80`
                  }`}
                >
                  {category.name} ({category.count})
                </button>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* 统计信息 */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <StatCard
          icon={<Search className="text-blue-600" size={24} />}
          title="搜索结果"
          value={filteredPrompts.length}
          subtitle="个提示词"
        />
        <StatCard
          icon={<TrendingUp className="text-green-600" size={24} />}
          title="热门标签"
          value={filteredPrompts.filter(p => p.popularity > 80).length}
          subtitle="高热度"
        />
        <StatCard
          icon={<Star className="text-yellow-600" size={24} />}
          title="我的收藏"
          value={favorites.length}
          subtitle="已收藏"
        />
        <StatCard
          icon={<Copy className="text-purple-600" size={24} />}
          title="总使用率"
          value="78%"
          subtitle="平均使用率"
        />
      </div>

      {/* 提示词列表 */}
      <div className="space-y-4">
        {filteredPrompts.length === 0 ? (
          <div className="text-center py-12 bg-white/80 backdrop-blur-sm rounded-xl border border-gray-200">
            <Search className="mx-auto text-gray-400 mb-4" size={48} />
            <p className="text-gray-500">未找到匹配的提示词，请尝试其他搜索条件</p>
          </div>
        ) : (
          filteredPrompts.map((prompt, index) => (
            <PromptCard
              key={index}
              prompt={prompt}
              isFavorite={favorites.includes(prompt.en)}
              onCopy={handleCopy}
              onToggleFavorite={() => toggleFavorite(prompt.en)}
              copyStatus={copyStatus}
            />
          ))
        )}
      </div>

      {/* 复制状态提示 */}
      {copyStatus && (
        <div className={`fixed bottom-4 right-4 px-4 py-2 rounded-lg text-white text-sm transition-all duration-300 shadow-lg ${
          copyStatus === 'copied' ? 'bg-green-600' : 'bg-red-600'
        }`}>
          {copyStatus === 'copied' ? '✅ 已复制到剪贴板' : '❌ 复制失败'}
        </div>
      )}
    </div>
  );
};

/**
 * 统计卡片组件
 */
const StatCard = ({ icon, title, value, subtitle }) => (
  <div className="bg-white/80 backdrop-blur-sm rounded-xl p-4 border border-gray-200 shadow-sm">
    <div className="flex items-center justify-between">
      <div>
        <p className="text-sm text-gray-600">{title}</p>
        <p className="text-2xl font-bold text-gray-900">{value}</p>
        <p className="text-xs text-gray-500">{subtitle}</p>
      </div>
      <div className="opacity-80">{icon}</div>
    </div>
  </div>
);

/**
 * 提示词卡片组件
 */
const PromptCard = ({ prompt, isFavorite, onCopy, onToggleFavorite, copyStatus }) => (
  <div className="bg-white/80 backdrop-blur-sm rounded-xl p-6 border border-gray-200 shadow-sm hover:shadow-md transition-all">
    <div className="flex justify-between items-start mb-4">
      <div className="flex-1">
        <div className="flex items-center gap-3 mb-2">
          <span className={`px-3 py-1 text-xs rounded-full ${
            prompt.category === 'Quality' ? 'bg-purple-100 text-purple-700' :
            prompt.category === 'Character' ? 'bg-blue-100 text-blue-700' :
            prompt.category === 'Effects' ? 'bg-orange-100 text-orange-700' :
            prompt.category === 'Scene' ? 'bg-green-100 text-green-700' :
            prompt.category === 'Style' ? 'bg-pink-100 text-pink-700' :
            'bg-gray-100 text-gray-700'
          }`}>
            {prompt.category}
          </span>
          <div className="flex items-center gap-1">
            <TrendingUp size={14} className="text-green-600" />
            <span className="text-sm font-medium text-green-600">{prompt.usage}</span>
          </div>
          <div className="flex items-center gap-1">
            <div className="w-16 bg-gray-200 rounded-full h-2">
              <div 
                className="bg-orange-500 h-2 rounded-full" 
                style={{ width: `${prompt.popularity}%` }}
              />
            </div>
            <span className="text-xs text-gray-500">{prompt.popularity}%</span>
          </div>
        </div>
      </div>
      <div className="flex gap-2">
        <button
          onClick={onToggleFavorite}
          className={`p-2 rounded-lg transition-colors ${
            isFavorite 
              ? 'bg-red-100 text-red-600 hover:bg-red-200' 
              : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
          }`}
          title={isFavorite ? '取消收藏' : '添加收藏'}
        >
          <Heart size={16} fill={isFavorite ? 'currentColor' : 'none'} />
        </button>
        <button
          onClick={() => onCopy(prompt.en)}
          className="p-2 bg-blue-100 text-blue-600 rounded-lg hover:bg-blue-200 transition-colors"
          title="复制提示词"
        >
          {copyStatus === 'copied' ? (
            <CheckCircle size={16} />
          ) : (
            <Copy size={16} />
          )}
        </button>
      </div>
    </div>

    <div className="space-y-3">
      {/* 英文提示词 */}
      <div className="bg-gray-50 p-4 rounded-lg">
        <p className="text-sm font-mono text-gray-800 leading-relaxed">{prompt.en}</p>
      </div>
      
      {/* 中文翻译 */}
      <div className="bg-blue-50 p-3 rounded-lg">
        <p className="text-sm text-blue-800">
          <span className="font-medium">中文释义：</span>{prompt.cn}
        </p>
      </div>
    </div>
  </div>
);

export default PromptLibraryPage;
