        }
      }
    }

    // ÊèêÂèñÂÖ∂‰ªñÂèÇÊï∞...
    if (['KSampler', 'KSamplerAdvanced', 'SamplerCustom'].includes(nodeData.class_type)) {
      const inputs = nodeData.inputs;
      if (inputs.seed !== undefined) parameters.seed = inputs.seed;
