}

// Ëß£ÊûêNovelAIÊï∞ÊçÆ
function parseNovelAI(text) {
  const result = {
    positive: '',
    negative: '',
    parameters: {}
  };
  try {
    const data = JSON.parse(text);
    result.positive = data.prompt || '';
    result.negative = data.uc || '';
