// ‰∏ªË¶ÅÁöÑÊèêÂèñÂáΩÊï∞
export async function extractMetadata(file) {
  console.log('üöÄ ÂºÄÂßãÊèêÂèñÂõæÂÉèÂÖÉÊï∞ÊçÆ...');

  const result = {
    success: false,
    filename: file.name,
    extractedData: {},
