            break;
          }
        }
      }

      // ÊñπÂºè2: Áõ¥Êé•ÁöÑtextÂ±ûÊÄß
      if (!text && node.text && typeof node.text === 'string') {
        text = node.text.trim();
        console.log(`üìÑ ‰ªétextÂ±ûÊÄßËé∑ÂèñÊñáÊú¨: ${text.substring(0, 50)}...`);
      }

      // ÊñπÂºè3: inputs‰∏≠ÁöÑtext
      if (!text && node.inputs) {
