  const finalNegative = smartMergePrompts(negativePrompts);

  console.log(`üéØ ÊúÄÁªàÁªìÊûú: Ê≠£ÂêëÊèêÁ§∫ËØç${finalPositive.length}Â≠óÁ¨¶, Ë¥üÂêëÊèêÁ§∫ËØç${finalNegative.length}Â≠óÁ¨¶`);

  return {
    positive: finalPositive,
    negative: finalNegative,
    parameters
  };
}

// Êô∫ËÉΩÂêàÂπ∂ÊèêÁ§∫ËØç
function smartMergePrompts(prompts) {
  if (prompts.length === 0) return '';
  if (prompts.length === 1) return prompts[0];

  // ÂéªÈáçÂπ∂ÊåâÈïøÂ∫¶ÊéíÂ∫èÔºå‰ºòÂÖàÈÄâÊã©Êõ¥ÂÆåÊï¥ÁöÑÊèêÁ§∫ËØç
  const uniquePrompts = [...new Set(prompts)];
  uniquePrompts.sort((a, b) => b.length - a.length);

