const PromptGeneratorPage = () => {
  const {
    inputText,
    generatedPrompt,
    selectedStyle,
    isGenerating,
    generationSource,
    generationCount,
    apiError,
    validationErrors,
    setInputText,
    setSelectedStyle,
    generatePrompt,
    copyPrompt,
    insertTag
  } = usePromptGenerator();

  const [showQuickTags, setShowQuickTags] = useState(false);
  const [copyStatus, setCopyStatus] = useState('');

  const handleCopy = async (text) => {
    const success = await copyPrompt(text);
    setCopyStatus(success ? 'copied' : 'error');
    setTimeout(() => setCopyStatus(''), 2000);
  };

  const handleGenerate = async () => {
    await generatePrompt();
  };

  return (
    <div className="space-y-6">
      {/* 页面标题 */}
      <div className="text-center mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2 flex items-center justify-center">
          <Wand2 className="mr-3 text-purple-600" size={32} />
          智能提示词生成器
        </h1>
        <p className="text-gray-600 max-w-2xl mx-auto">
          基于AI技术的智能提示词生成，支持中文输入自动转换为英文标签，为您的AI绘画提供专业的提示词支持
        </p>
      </div>

      {/* 主要内容区域 */}
      <div className="bg-white/80 backdrop-blur-sm rounded-xl shadow-lg border border-purple-100/50 p-6">
        <div className="space-y-6">
          {/* 输入区域 */}
          <div>
            <div className="flex justify-between items-center mb-3">
              <label className="block text-sm font-medium text-gray-700">
                描述你想要的画面
              </label>
              <span className="text-xs text-gray-500">
                {inputText.length}/{APP_CONFIG.MAX_INPUT_LENGTH} 字符
              </span>
            </div>
            <textarea
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              placeholder="例如：一个可爱的猫女孩，穿着白色连衣裙，在樱花树下看书..."
              maxLength={APP_CONFIG.MAX_INPUT_LENGTH}
              className="w-full h-32 px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent resize-none transition-all bg-gradient-to-br from-white to-purple-50/30"
            />
            
            {/* 验证错误显示 */}
            {validationErrors.length > 0 && (
              <div className="mt-2 text-sm text-red-600 space-y-1">
                {validationErrors.map((error, idx) => (
                  <div key={idx} className="flex items-center">
                    <AlertCircle size={14} className="mr-1" />
                    {error}
                  </div>
                ))}
              </div>
            )}

            {/* 智能预览 */}
            {inputText.length > 0 && (
              <div className="mt-3 p-3 bg-blue-50 border border-blue-200 rounded-lg">
                <span className="text-sm font-medium text-blue-800">智能预览：</span>
                <div className="mt-1 flex flex-wrap gap-1">
                  {inputText.match(/女孩|男孩|人物|角色|美女|帅哥|少女|boy|girl|character|人|风景|森林|海边|城市|建筑|landscape|forest|ocean|city|building|猫|狗|动物|cat|dog|animal|花|flower|树|tree|天空|sky|夜晚|night|白天|day/gi)?.map((word, idx) => (
                    <span key={idx} className="inline-block px-2 py-0.5 bg-blue-100 text-blue-700 rounded text-xs">
                      {word}
                    </span>
                  )) || <span className="text-blue-600 text-sm">输入更多内容以获得智能提示</span>}
                </div>
              </div>
            )}
          </div>

          {/* 风格选择 */}
          <div className="space-y-3">
            <label className="block text-sm font-medium text-gray-700">选择艺术风格：</label>
            <div className="grid grid-cols-2 md:grid-cols-5 gap-2">
              {PAINTING_STYLES.map(style => (
                <button 
                  key={style.name} 
                  onClick={() => setSelectedStyle(selectedStyle === style.name ? '' : style.name)}
                  className={`p-3 text-sm rounded-lg border transition-all ${
                    selectedStyle === style.name 
                      ? 'bg-purple-600 text-white border-purple-600 shadow-md' 
                      : 'bg-white hover:bg-purple-50 text-gray-700 border-gray-200 hover:border-purple-300'
                  }`}
                >
                  <div className="font-medium">{style.name.split(' ')[0]}</div>
                  <div className="text-xs opacity-75">{style.name.split(' ')[1]}</div>
                </button>
              ))}
            </div>
          </div>

          {/* 快速标签区域 */}
          <QuickTagsSection 
            showQuickTags={showQuickTags}
            setShowQuickTags={setShowQuickTags}
            insertTag={insertTag}
          />

          {/* 生成按钮 */}
          <button
            onClick={handleGenerate}
            disabled={!inputText.trim() || isGenerating}
            className="w-full bg-gradient-to-r from-purple-600 via-purple-700 to-blue-600 text-white py-4 px-6 rounded-lg font-medium hover:shadow-lg hover:scale-105 transition-all disabled:opacity-50 disabled:cursor-not-allowed relative overflow-hidden"
          >
            <div className="relative z-10 flex items-center justify-center">
              {isGenerating ? (
                <>
                  <div className="animate-spin inline-block w-5 h-5 border-2 border-white border-t-transparent rounded-full mr-3"></div>
                  AI智能生成中...
                </>
              ) : (
                <>
                  <Sparkles className="inline mr-3" size={20} />
                  AI智能生成提示词
                </>
              )}
            </div>
          </button>

          {/* API错误提示 */}
          {apiError && (
            <div className="p-3 bg-orange-50 border border-orange-200 rounded-lg">
              <div className="flex items-start">
                <AlertCircle className="mr-2 text-orange-600 flex-shrink-0 mt-0.5" size={16} />
                <p className="text-sm text-orange-800">{apiError}</p>
              </div>
            </div>
          )}
        </div>

        {/* 生成结果 */}
        {generatedPrompt && (
          <GeneratedResult
            prompt={generatedPrompt}
            source={generationSource}
            count={generationCount}
            onCopy={handleCopy}
            onRegenerate={handleGenerate}
            copyStatus={copyStatus}
            isRegenerating={isGenerating}
          />
        )}
      </div>
    </div>
  );
};

/**
 * 快速标签区域组件
 */
const QuickTagsSection = ({ showQuickTags, setShowQuickTags, insertTag }) => (
  <div className="border-t pt-4">
    <div className="flex justify-between items-center mb-3">
      <span className="text-sm font-medium text-gray-700">快速插入标签</span>
      <button
        onClick={() => setShowQuickTags(!showQuickTags)}
        className="text-sm text-purple-600 hover:text-purple-800 transition-colors px-3 py-1 rounded-md hover:bg-purple-50"
      >
        {showQuickTags ? '收起标签' : '展开标签'}
      </button>
    </div>
    {showQuickTags && (
      <div className="space-y-4">
        {QUICK_TAGS.map((category, idx) => (
          <div key={idx} className="space-y-2">
            <span className="text-sm font-medium text-gray-600 block">{category.category}</span>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
              {category.tags.map((tag, tagIdx) => (
                <button
                  key={tagIdx}
                  onClick={() => insertTag(tag)}
                  className="p-3 text-xs bg-gradient-to-r from-gray-50 to-gray-100 hover:from-purple-50 hover:to-purple-100 text-gray-700 rounded-lg border border-gray-200 hover:border-purple-300 transition-all group text-left"
                  title={`点击插入: ${tag.en}`}
                >
                  <div className="font-medium text-gray-800">{tag.en}</div>
                  <div className="text-gray-500 text-xs group-hover:text-purple-600">({tag.cn})</div>
                </button>
              ))}
            </div>
          </div>
        ))}
      </div>
    )}
  </div>
);

/**
 * 生成结果显示组件
 */
const GeneratedResult = ({ prompt, source, count, onCopy, onRegenerate, copyStatus, isRegenerating }) => (
  <div className="mt-8 p-6 bg-gradient-to-br from-green-50 to-emerald-50 border border-green-200/50 rounded-lg shadow-sm">
    <div className="flex justify-between items-start mb-4">
      <h3 className="font-medium text-gray-900 flex items-center">
        <Sparkles className="mr-2 text-green-600" size={18} />
        生成的提示词
        <span className={`ml-3 text-xs px-3 py-1 rounded-full ${
          source === 'ai' 
            ? 'bg-purple-100 text-purple-700' 
            : 'bg-green-100 text-green-700'
        }`}>
          {source === 'ai' ? '🤖 AI智能生成' : '⚡ 本地增强'}
        </span>
      </h3>
      <div className="flex gap-2">
        <button 
          onClick={onRegenerate}
          disabled={isRegenerating}
          className="p-2 hover:bg-green-100 rounded-lg transition-colors group disabled:opacity-50"
          title="重新生成"
        >
          <RefreshCw size={16} className={`text-gray-600 group-hover:text-green-600 ${isRegenerating ? 'animate-spin' : ''}`} />
        </button>
        <button 
          onClick={() => onCopy(prompt)}
          className="p-2 hover:bg-green-100 rounded-lg transition-colors group"
          title="复制到剪贴板"
        >
          {copyStatus === 'copied' ? (
            <CheckCircle size={16} className="text-green-600" />
          ) : copyStatus === 'error' ? (
            <AlertCircle size={16} className="text-red-600" />
          ) : (
            <Copy size={16} className="text-gray-600 group-hover:text-green-600" />
          )}
        </button>
      </div>
    </div>
    
    <div className="bg-white p-4 rounded-lg border border-green-200/30 mb-4">
      <p className="text-sm text-gray-700 leading-relaxed font-mono">{prompt}</p>
    </div>
    
    <div className="flex justify-between items-center text-sm text-gray-600">
      <div className="flex gap-6">
        <span>字符数：<span className="font-medium">{prompt.length}</span></span>
        <span>标签数：<span className="font-medium">{prompt.split(',').length}</span></span>
        <span className={`font-medium ${source === 'ai' ? 'text-purple-600' : 'text-green-600'}`}>
          {source === 'ai' ? '🤖 DeepSeek-R1' : '⚡ 本地算法'}
        </span>
      </div>
      {count > 0 && (
        <span className="text-xs bg-gray-100 px-2 py-1 rounded">
          第 {count} 次生成
        </span>
      )}
    </div>
  </div>
);

export default PromptGeneratorPage;
