// Ê£ÄÊµãÂèÇÊï∞Ë°å
function isParameterLine(line) {
  return /\b(Steps|Sampler|CFG scale|Seed|Size|Model):/i.test(line);
}

// Ëß£ÊûêÂèÇÊï∞
function parseParameters(paramText) {
  const parameters = {};
  const regex = /(\w+(?:\s+\w+)*)\s*:\s*([^,]+?)(?=,\s*\w+\s*:|$)/g;
  let match;

  while ((match = regex.exec(paramText)) !== null) {
    const key = match[1].trim().toLowerCase().replace(/\s+/g, '');
    const value = match[2].trim();

    const numValue = parseFloat(value);
    parameters[key] = !isNaN(numValue) && isFinite(numValue) ? numValue : value;
  }

  return parameters;
}

// Ëß£ÊûêComfyUIÊï∞ÊçÆ
function parseComfyUI(text) {
  const result = {
    positive: '',
    negative: '',
    parameters: {}
  };

  try {
    const data = JSON.parse(text);

    if (Array.isArray(data?.nodes)) {
      // Â∑•‰ΩúÊµÅÊ†ºÂºè
      const prompts = extractFromComfyWorkflow(data);
      result.positive = prompts.positive;
      result.negative = prompts.negative;
    } else if (typeof data === 'object') {
      // ÊèêÁ§∫Ê†ºÂºè
      const prompts = extractFromComfyPrompt(data);
      result.positive = prompts.positive;
      result.negative = prompts.negative;
    }
  } catch (error) {
    console.warn('ComfyUIËß£ÊûêÂ§±Ë¥•:', error);
  }

  return result;
}

// ‰ªéComfyUIÂ∑•‰ΩúÊµÅÊèêÂèñ
function extractFromComfyWorkflow(workflow) {
  const positivePrompts = [];
  const negativePrompts = [];

  for (const node of workflow.nodes || []) {
    if (['CLIPTextEncode', 'CLIPTextEncodeSDXL'].includes(node.type)) {
      const text = node.widgets_values?.[0];
      if (text && typeof text === 'string' && text.trim()) {
        const cleanText = text.trim();

        if (isNegativePrompt(cleanText) || 
            node.title?.toLowerCase().includes('negative')) {
          negativePrompts.push(cleanText);
        } else {
          positivePrompts.push(cleanText);
        }
      }
    }
  }

  return {
    positive: positivePrompts.join(', '),
    negative: negativePrompts.join(', ')
  };
}

// ‰ªéComfyUIÊèêÁ§∫ÊèêÂèñ
function extractFromComfyPrompt(prompt) {
  const positivePrompts = [];
  const negativePrompts = [];

  for (const [nodeId, nodeData] of Object.entries(prompt)) {
    if (['CLIPTextEncode', 'CLIPTextEncodeSDXL'].includes(nodeData.class_type)) {
      const text = nodeData.inputs?.text;
      if (text && typeof text === 'string' && text.trim()) {
        const cleanText = text.trim();

        if (isNegativePrompt(cleanText)) {
          negativePrompts.push(cleanText);
        } else {
          positivePrompts.push(cleanText);
        }
      }
    }
  }

  return {
    positive: positivePrompts.join(', '),
    negative: negativePrompts.join(', ')
  };
}

// Ê£ÄÊµãË¥üÂêëÊèêÁ§∫ËØç
function isNegativePrompt(text) {
  const lowerText = text.toLowerCase();
  const negativeKeywords = [
    'worst quality', 'bad anatomy', 'ugly', 'blurry', 'lowres',
    'bad hands', 'text', 'error', 'cropped'
  ];

  return negativeKeywords.some(keyword => lowerText.includes(keyword));
}

