import React, { useState, useRef, useEffect } from 'react';
import { 
  Plus, Search, Copy, Heart, X, Languages, 
  ChevronDown, ChevronRight, Tag as TagIcon,
  Sparkles, Bookmark, Clock, TrendingUp
} from 'lucide-react';
import { copyToClipboard } from '../utils/clipboard';
import { translateText } from '../services/aliTranslationService';
import { TAG_DATABASE, searchTags, getPopularTags } from '../constants/tagDatabase';

const PromptLibraryPage = () => {
  // 提示词编辑状态
  const [prompt, setPrompt] = useState('');
  const [translatedPrompt, setTranslatedPrompt] = useState('');
  const [isTranslating, setIsTranslating] = useState(false);
  const [promptHistory, setPromptHistory] = useState([]);
  
  // 标签库状态
  const [selectedCategory, setSelectedCategory] = useState('favorites');
  const [selectedSubcategory, setSelectedSubcategory] = useState(null);
  const [expandedCategories, setExpandedCategories] = useState({ favorites: true });
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  
  // UI状态
  const [favorites, setFavorites] = useState([]);
  const [copyStatus, setCopyStatus] = useState('');
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [cursorPosition, setCursorPosition] = useState(0);
  
  const textareaRef = useRef(null);
  const suggestionsRef = useRef(null);

  // 实时搜索建议
  useEffect(() => {
    if (searchQuery.trim()) {
      const results = searchTags(searchQuery).slice(0, 20);
      setSearchResults(results);
    } else {
      setSearchResults([]);
    }
  }, [searchQuery]);

  // 处理提示词变化
  const handlePromptChange = (value) => {
    setPrompt(value);
    
    // 添加到历史记录
    if (value.trim() && !promptHistory.includes(value.trim())) {
      setPromptHistory(prev => [value.trim(), ...prev.slice(0, 9)]);
    }
  };

  // 翻译功能
  const handleTranslate = async () => {
    if (!prompt.trim()) return;
    
    setIsTranslating(true);
    try {
      const translated = await translateText(prompt);
      setTranslatedPrompt(translated);
    } catch (error) {
      console.error('翻译失败:', error);
      setTranslatedPrompt('翻译失败，请重试');
    } finally {
      setIsTranslating(false);
    }
  };

  // 添加标签到提示词
  const addTagToPrompt = (tag) => {
    const newPrompt = prompt ? `${prompt}, ${tag.en}` : tag.en;
    setPrompt(newPrompt);
    
    // 聚焦到文本框末尾
    if (textareaRef.current) {
      textareaRef.current.focus();
      textareaRef.current.setSelectionRange(newPrompt.length, newPrompt.length);
    }
  };

  // 复制功能
  const handleCopy = async (text) => {
    const success = await copyToClipboard(text || prompt);
    setCopyStatus(success ? 'copied' : 'error');
    setTimeout(() => setCopyStatus(''), 2000);
  };

  // 收藏功能
  const toggleFavorite = (tag) => {
    setFavorites(prev => {
      const isAlreadyFavorited = prev.some(fav => fav.en === tag.en);
      if (isAlreadyFavorited) {
        return prev.filter(fav => fav.en !== tag.en);
      } else {
        return [...prev, tag];
      }
    });
  };

  // 分类展开/收起
  const toggleCategory = (categoryKey) => {
    setExpandedCategories(prev => ({
      ...prev,
      [categoryKey]: !prev[categoryKey]
    }));
  };

  // 选择分类
  const selectCategory = (categoryKey, subcategoryKey = null) => {
    setSelectedCategory(categoryKey);
    setSelectedSubcategory(subcategoryKey);
  };

  // 获取当前显示的标签
  const getCurrentTags = () => {
    if (searchQuery.trim()) {
      return searchResults;
    }
    
    if (selectedCategory === 'favorites') {
      if (selectedSubcategory === 'personal') {
        return favorites;
      } else {
        // 显示热门标签
        return getPopularTags(20);
      }
    }
    
    const category = TAG_DATABASE[selectedCategory];
    if (!category) return [];
    
    if (selectedSubcategory) {
      const subcategory = category.subcategories[selectedSubcategory];
      return subcategory ? subcategory.tags : [];
    }
    
    // 返回该分类下所有标签
    const allTags = [];
    Object.values(category.subcategories).forEach(subcategory => {
      allTags.push(...subcategory.tags);
    });
    return allTags;
  };

  // 动态更新TAG_DATABASE中的收藏标签
  useEffect(() => {
    if (TAG_DATABASE.favorites && TAG_DATABASE.favorites.subcategories.personal) {
      TAG_DATABASE.favorites.subcategories.personal.tags = favorites;
    }
  }, [favorites]);

  const currentTags = getCurrentTags();

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50">
      {/* 页面标题 */}
      <div className="bg-white/80 backdrop-blur-sm border-b border-gray-200 px-6 py-4">
        <div className="max-w-7xl mx-auto">
          <h1 className="text-2xl font-bold text-gray-900 mb-2 flex items-center">
            <Sparkles className="text-blue-600 mr-3" size={28} />
            智能提示词库 3.0
          </h1>
          <p className="text-gray-600">
            专业的提示词编辑和管理工具 • 支持实时翻译和智能分类 • 让AI绘画创作更高效
          </p>
        </div>
      </div>

      <div className="max-w-7xl mx-auto p-6">
        {/* 提示词编辑栏 */}
        <div className="bg-white rounded-xl shadow-lg border border-gray-200 mb-6">
          <div className="p-6">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-lg font-semibold text-gray-900 flex items-center">
                <TagIcon className="text-blue-600 mr-2" size={20} />
                提示词编辑器
              </h2>
              <div className="flex items-center gap-2">
                <button
                  onClick={handleTranslate}
                  disabled={isTranslating || !prompt.trim()}
                  className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 transition-colors"
                >
                  <Languages size={16} />
                  {isTranslating ? '翻译中...' : '翻译'}
                </button>
                <button
                  onClick={() => handleCopy()}
                  className="flex items-center gap-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
                >
                  <Copy size={16} />
                  复制
                </button>
                <button
                  onClick={() => setPrompt('')}
                  className="p-2 text-gray-500 hover:bg-gray-100 rounded-lg transition-colors"
                >
                  <X size={16} />
                </button>
              </div>
            </div>

            {/* 提示词输入区 */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
              {/* 英文输入区 */}
              <div className="space-y-2">
                <label className="text-sm font-medium text-gray-700">英文提示词</label>
                <textarea
                  ref={textareaRef}
                  value={prompt}
                  onChange={(e) => handlePromptChange(e.target.value)}
                  placeholder="输入或点击标签添加提示词..."
                  className="w-full h-32 p-4 border border-gray-300 rounded-lg resize-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all font-mono text-sm"
                />
                <div className="flex items-center justify-between text-xs text-gray-500">
                  <span>字符数: {prompt.length}</span>
                  <span>标签数: {prompt.split(',').filter(t => t.trim()).length}</span>
                </div>
              </div>

              {/* 中文翻译区 */}
              <div className="space-y-2">
                <label className="text-sm font-medium text-gray-700">中文翻译</label>
                <div className="h-32 p-4 bg-blue-50 border border-blue-200 rounded-lg overflow-y-auto">
                  {translatedPrompt ? (
                    <div className="space-y-2">
                      {translatedPrompt.split(',').map((tag, index) => (
                        <div key={index} className="inline-block mr-2 mb-2">
                          <div className="bg-white rounded-lg p-2 border border-blue-200 shadow-sm">
                            <div className="text-sm font-medium text-gray-900">
                              {prompt.split(',')[index]?.trim() || ''}
                            </div>
                            <div className="text-xs text-blue-600 mt-1">
                              {tag.trim()}
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-gray-500 text-sm">
                      点击"翻译"按钮查看中文释义
                    </div>
                  )}
                </div>
              </div>
            </div>

            {/* 快速历史记录 */}
            {promptHistory.length > 0 && (
              <div className="mt-4 p-4 bg-gray-50 rounded-lg">
                <div className="flex items-center gap-2 mb-2">
                  <Clock size={16} className="text-gray-600" />
                  <span className="text-sm font-medium text-gray-700">最近使用</span>
                </div>
                <div className="flex flex-wrap gap-2">
                  {promptHistory.slice(0, 5).map((historyPrompt, index) => (
                    <button
                      key={index}
                      onClick={() => setPrompt(historyPrompt)}
                      className="px-3 py-1 text-xs bg-white border border-gray-200 rounded-full hover:bg-blue-50 hover:border-blue-300 transition-colors truncate max-w-xs"
                    >
                      {historyPrompt}
                    </button>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>

        {/* 主要内容区域 */}
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          {/* 左侧分类导航 */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-4 sticky top-6">
              <div className="mb-4">
                <div className="relative">
                  <Search className="absolute left-3 top-3 text-gray-400" size={16} />
                  <input
                    type="text"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    placeholder="搜索标签..."
                    className="w-full pl-9 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent text-sm"
                  />
                </div>
              </div>

              <div className="space-y-1">
                {Object.entries(TAG_DATABASE).map(([categoryKey, category]) => (
                  <div key={categoryKey}>
                    <button
                      onClick={() => toggleCategory(categoryKey)}
                      className={`w-full flex items-center justify-between p-3 rounded-lg transition-colors ${
                        selectedCategory === categoryKey && !selectedSubcategory
                          ? 'bg-blue-50 text-blue-700 border border-blue-200'
                          : 'hover:bg-gray-50'
                      }`}
                    >
                      <div className="flex items-center gap-2">
                        <span className="text-lg">{category.icon}</span>
                        <span className="font-medium">{category.name}</span>
                      </div>
                      {expandedCategories[categoryKey] ? (
                        <ChevronDown size={16} />
                      ) : (
                        <ChevronRight size={16} />
                      )}
                    </button>

                    {expandedCategories[categoryKey] && (
                      <div className="ml-4 mt-1 space-y-1">
                        {Object.entries(category.subcategories).map(([subcategoryKey, subcategory]) => (
                          <button
                            key={subcategoryKey}
                            onClick={() => selectCategory(categoryKey, subcategoryKey)}
                            className={`w-full text-left p-2 rounded-lg text-sm transition-colors ${
                              selectedCategory === categoryKey && selectedSubcategory === subcategoryKey
                                ? 'bg-blue-50 text-blue-700'
                                : 'hover:bg-gray-50 text-gray-600'
                            }`}
                          >
                            {subcategory.name}
                          </button>
                        ))}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* 右侧标签展示区 */}
          <div className="lg:col-span-3">
            <div className="bg-white rounded-xl shadow-lg border border-gray-200">
              <div className="p-6 border-b border-gray-200">
                <div className="flex items-center justify-between">
                  <div>
                    <h3 className="text-lg font-semibold text-gray-900">
                      {searchQuery ? '搜索结果' : 
                       selectedSubcategory ? 
                       `${TAG_DATABASE[selectedCategory]?.name} - ${TAG_DATABASE[selectedCategory]?.subcategories[selectedSubcategory]?.name}` :
                       TAG_DATABASE[selectedCategory]?.name || '标签库'}
                    </h3>
                    <p className="text-sm text-gray-600 mt-1">
                      {searchQuery ? `找到 ${currentTags.length} 个相关标签` : 
                       `共 ${currentTags.length} 个标签`}
                    </p>
                  </div>
                  <div className="flex items-center gap-2">
                    <span className="text-xs bg-blue-100 text-blue-600 px-2 py-1 rounded-full">
                      点击标签添加到提示词
                    </span>
                  </div>
                </div>
              </div>

              <div className="p-6">
                {currentTags.length === 0 ? (
                  <div className="text-center py-12">
                    <TagIcon className="mx-auto text-gray-400 mb-4" size={48} />
                    <p className="text-gray-500 mb-4">
                      {searchQuery ? '未找到匹配的标签' : '该分类暂无标签'}
                    </p>
                  </div>
                ) : (
                  <div className="grid grid-cols-1 sm:grid-cols-2 xl:grid-cols-3 gap-3">
                    {currentTags.map((tag, index) => (
                      <TagCard
                        key={`${tag.en}-${index}`}
                        tag={tag}
                        onAdd={() => addTagToPrompt(tag)}
                        onToggleFavorite={() => toggleFavorite(tag)}
                        isFavorited={favorites.some(fav => fav.en === tag.en)}
                      />
                    ))}
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* 复制状态提示 */}
      {copyStatus && (
        <div className={`fixed bottom-4 right-4 px-4 py-2 rounded-lg text-white text-sm transition-all duration-300 shadow-lg z-50 ${
          copyStatus === 'copied' ? 'bg-green-600' : 'bg-red-600'
        }`}>
          {copyStatus === 'copied' ? '✅ 已复制到剪贴板' : '❌ 复制失败'}
        </div>
      )}
    </div>
  );
};

/**
 * 标签卡片组件
 */
const TagCard = ({ tag, onAdd, onToggleFavorite, isFavorited }) => (
  <div className="group bg-gradient-to-r from-gray-50 to-white border border-gray-200 rounded-lg p-4 hover:shadow-md hover:border-blue-300 transition-all duration-200 cursor-pointer">
    <div onClick={onAdd} className="flex-1">
      <div className="flex items-start justify-between mb-2">
        <div className="flex-1">
          <h4 className="font-medium text-gray-900 text-sm mb-1 group-hover:text-blue-700 transition-colors">
            {tag.en}
          </h4>
          <p className="text-xs text-blue-600 mb-2">
            {tag.cn}
          </p>
        </div>
        <button
          onClick={(e) => {
            e.stopPropagation();
            onToggleFavorite();
          }}
          className={`p-1 rounded-full transition-colors ${
            isFavorited 
              ? 'text-red-500 hover:bg-red-50' 
              : 'text-gray-400 hover:bg-gray-100 hover:text-red-500'
          }`}
        >
          <Heart size={14} fill={isFavorited ? 'currentColor' : 'none'} />
        </button>
      </div>
      
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <div className="flex items-center gap-1">
            <TrendingUp size={12} className="text-green-600" />
            <span className="text-xs text-green-600 font-medium">{tag.frequency}%</span>
          </div>
          <div className="w-12 bg-gray-200 rounded-full h-1">
            <div 
              className="bg-green-500 h-1 rounded-full transition-all" 
              style={{ width: `${tag.frequency}%` }}
            />
          </div>
        </div>
        
        <button
          onClick={onAdd}
          className="opacity-0 group-hover:opacity-100 transition-opacity text-xs bg-blue-600 text-white px-2 py-1 rounded-full hover:bg-blue-700"
        >
          <Plus size={10} className="inline mr-1" />
          添加
        </button>
      </div>
    </div>
  </div>
);

export default PromptLibraryPage;
