import exifr from 'exifr';

/**
 * ‰∏ì‰∏öÁ∫ßÂõæÂÉèÂÖÉÊï∞ÊçÆÊèêÂèñÂô® v2.0
 * Âü∫‰∫éstable-diffusion-inspector„ÄÅauto1111-pnginfoÁ≠âÂºÄÊ∫êÈ°πÁõÆÁöÑÊúÄ‰Ω≥ÂÆûË∑µ
 * ‰∏ìÈó®Ëß£ÂÜ≥PNGÊñáÊú¨ÂùóËß£Êûê„ÄÅÂ≠óÁ¨¶ÁºñÁ†ÅÂíåÊèêÁ§∫ËØçÊà™Êñ≠ÈóÆÈ¢ò
 */

/**
 * ‰∏ªÊèêÂèñÊñπÊ≥ï
 */
async function extractMetadata(file) {
  console.log('üöÄ ÂºÄÂßã‰∏ì‰∏öÁ∫ßÂÖÉÊï∞ÊçÆÊèêÂèñ...');
  
  const result = {
    success: false,
    filename: file.name,
    extractedData: {},
    standardizedData: null,
    errors: []
  };

  try {
    // Âπ∂Ë°åËøêË°åÂ§öÁßçÊèêÂèñÊñπÊ≥ï
    const extractionPromises = [
      extractFromEXIF(file),
      file.type === 'image/png' ? extractFromPNGChunks(file) : null
    ].filter(Boolean);

    const extractionResults = await Promise.allSettled(extractionPromises);
    
    // Êï¥ÂêàÁªìÊûú
    extractionResults.forEach((promiseResult, index) => {
      if (promiseResult.status === 'fulfilled' && promiseResult.value) {
        const methodNames = ['EXIF', 'PNG_Chunks'];
        const methodName = methodNames[index];
        result.extractedData[methodName] = promiseResult.value;
      }
    });

    // Ê†áÂáÜÂåñÊï∞ÊçÆ
    result.standardizedData = standardizeExtractedData(result.extractedData);
    result.success = Object.keys(result.extractedData).length > 0;

    return result;
    
  } catch (error) {
    console.error('‚ùå ÂÖÉÊï∞ÊçÆÊèêÂèñÂ§±Ë¥•:', error);
    result.errors.push(`ÊÄª‰ΩìÈîôËØØ: ${error.message}`);
    throw new Error(`ÂÖÉÊï∞ÊçÆÊèêÂèñÂ§±Ë¥•: ${error.message}`);
  }
}

/**
 * ‰∏ì‰∏öÁ∫ßPNGÊñáÊú¨ÂùóÊèêÂèñÂô® - ‰øÆÂ§çÁâπÊÆäÂ≠óÁ¨¶Êà™Êñ≠ÈóÆÈ¢ò
 */
async function extractFromPNGChunks(file) {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    
    reader.onload = (e) => {
      try {
        const buffer = e.target.result;
        const view = new DataView(buffer);
        
        // PNGÁ≠æÂêçÈ™åËØÅ
        const pngSignature = [0x89, 0x50, 0x4E, 0x47, 0x0D, 0x0A, 0x1A, 0x0A];
        for (let i = 0; i < 8; i++) {
          if (view.getUint8(i) !== pngSignature[i]) {
            throw new Error('‰∏çÊòØÊúâÊïàÁöÑPNGÊñá‰ª∂');
          }
        }

        const chunks = parsePNGChunks(buffer);
        const metadata = extractAIMetadataFromChunks(chunks);
        
        resolve({
          type: 'PNG_Chunks',
          confidence: metadata.confidence || 'high',
          data: metadata
        });
        
      } catch (error) {
        reject(error);
      }
    };
    
    reader.onerror = () => reject(new Error('Êñá‰ª∂ËØªÂèñÂ§±Ë¥•'));
    reader.readAsArrayBuffer(file);
  });
}

/**
 * È´òÁ∫ßPNGÂùóËß£ÊûêÂô® - ÂÆâÂÖ®Â§ÑÁêÜÊâÄÊúâÂ≠óÁ¨¶
 */
function parsePNGChunks(buffer) {
  const view = new DataView(buffer);
  const chunks = [];
  let offset = 8; // Ë∑≥ËøáPNGÁ≠æÂêç

  while (offset < buffer.byteLength - 8) {
    try {
      // ËØªÂèñÂùóÈïøÂ∫¶ (big-endian)
      const length = view.getUint32(offset);
      if (length > buffer.byteLength - offset) break; // ÂÆâÂÖ®Ê£ÄÊü•
      offset += 4;

      // ËØªÂèñÂùóÁ±ªÂûã
      const type = readChunkType(view, offset);
      offset += 4;

      // ËØªÂèñÂùóÊï∞ÊçÆ
      const data = new Uint8Array(buffer, offset, length);
      offset += length;

      // ËØªÂèñCRC
      const crc = view.getUint32(offset);
      offset += 4;

      chunks.push({ type, length, data, crc });

      if (type === 'IEND') break;
      
    } catch (error) {
      console.warn('PNGÂùóËß£ÊûêË≠¶Âëä:', error);
      break;
    }
  }

  return chunks;
}

/**
 * ÂÆâÂÖ®ÁöÑÂùóÁ±ªÂûãËØªÂèñ
 */
function readChunkType(view, offset) {
  const bytes = [];
  for (let i = 0; i < 4; i++) {
    bytes.push(view.getUint8(offset + i));
  }
  return String.fromCharCode(...bytes);
}

/**
 * ‰ªéPNGÂùóÊèêÂèñAIÂÖÉÊï∞ÊçÆ - ‰∏ìÈó®Â§ÑÁêÜSDÁîüÊàêÁöÑÂõæÂÉè
 */
function extractAIMetadataFromChunks(chunks) {
  const textChunks = chunks.filter(chunk => 
    ['tEXt', 'iTXt', 'zTXt'].includes(chunk.type)
  );

  const metadata = {
    confidence: 'low',
    generationTool: 'Unknown',
    positive: '',
    negative: '',
    parameters: {}
  };

  for (const chunk of textChunks) {
    const textData = parseTextChunk(chunk);
    if (!textData.keyword || !textData.text) continue;

    console.log('Ëß£ÊûêPNGÊñáÊú¨Âùó:', textData.keyword, 'ÈïøÂ∫¶:', textData.text.length);

    // Ê£ÄÊµã‰∏çÂêåAIÂ∑•ÂÖ∑ÁöÑÊï∞ÊçÆÊ†ºÂºè
    if (isAutomatic1111Format(textData)) {
      Object.assign(metadata, parseAutomatic1111Data(textData.text));
      metadata.generationTool = 'AUTOMATIC1111';
      metadata.confidence = 'high';
    } else if (isComfyUIFormat(textData)) {
      Object.assign(metadata, parseComfyUIData(textData.text));
      metadata.generationTool = 'ComfyUI';
      metadata.confidence = 'high';
    } else if (isNovelAIFormat(textData)) {
      Object.assign(metadata, parseNovelAIData(textData.text));
      metadata.generationTool = 'NovelAI';
      metadata.confidence = 'high';
    } else if (containsPromptLikeText(textData.text)) {
      metadata.positive = textData.text;
      metadata.confidence = 'medium';
    }
  }

  return metadata;
}

/**
 * È´òÁ∫ßÊñáÊú¨ÂùóËß£ÊûêÂô® - Ê≠£Á°ÆÂ§ÑÁêÜÊâÄÊúâÁºñÁ†ÅÂíåÁâπÊÆäÂ≠óÁ¨¶
 */
function parseTextChunk(chunk) {
  try {
    const data = chunk.data;
    
    if (chunk.type === 'tEXt') {
      return parseTEXt(data);
    } else if (chunk.type === 'iTXt') {
      return parseITXt(data);
    } else if (chunk.type === 'zTXt') {
      return parseZTXt(data);
    }
    
    return {};
  } catch (error) {
    console.warn('ÊñáÊú¨ÂùóËß£ÊûêÂ§±Ë¥•:', error);
    return {};
  }
}

/**
 * Ëß£ÊûêtEXtÂùó - Latin-1ÁºñÁ†ÅÔºåÂÆâÂÖ®Â§ÑÁêÜÁâπÊÆäÂ≠óÁ¨¶
 */
function parseTEXt(data) {
  const nullIndex = data.indexOf(0);
  if (nullIndex === -1) return {};

  const keyword = decodeLatin1(data.slice(0, nullIndex));
  const text = decodeLatin1(data.slice(nullIndex + 1));

  return { keyword, text };
}

/**
 * Ëß£ÊûêiTXtÂùó - UTF-8ÁºñÁ†Å
 */
function parseITXt(data) {
  try {
    const nullIndices = [];
    for (let i = 0; i < data.length; i++) {
      if (data[i] === 0) {
        nullIndices.push(i);
        if (nullIndices.length >= 4) break;
      }
    }

    if (nullIndices.length < 4) return {};

    const keyword = decodeUTF8(data.slice(0, nullIndices[0]));
    const text = decodeUTF8(data.slice(nullIndices[3] + 1));

    return { keyword, text };
  } catch (error) {
    console.warn('iTXtËß£ÊûêÂ§±Ë¥•:', error);
    return {};
  }
}

/**
 * Ëß£ÊûêzTXtÂùó - ÂéãÁº©ÊñáÊú¨
 */
function parseZTXt(data) {
  try {
    const nullIndex = data.indexOf(0);
    if (nullIndex === -1) return {};

    const keyword = decodeLatin1(data.slice(0, nullIndex));
    const compression = data[nullIndex + 1];
    
    if (compression !== 0) {
      return { keyword, text: '[ÂéãÁº©ÊñáÊú¨-ÊöÇ‰∏çÊîØÊåÅ]' };
    }

    return { keyword, text: '[ÈúÄË¶ÅzlibËß£ÂéãÁº©]', compressed: true };
  } catch (error) {
    console.warn('zTXtËß£ÊûêÂ§±Ë¥•:', error);
    return {};
  }
}

/**
 * ÂÆâÂÖ®ÁöÑLatin-1Ëß£Á†Å - ÈÄêÂ≠óËäÇÂ§ÑÁêÜÔºåÊ∞∏‰∏çÊà™Êñ≠
 */
function decodeLatin1(data) {
  let result = '';
  for (let i = 0; i < data.length; i++) {
    result += String.fromCharCode(data[i]);
  }
  return result;
}

/**
 * ÂÆâÂÖ®ÁöÑUTF-8Ëß£Á†Å
 */
function decodeUTF8(data) {
  try {
    return new TextDecoder('utf-8', { fatal: false }).decode(data);
  } catch (error) {
    console.warn('UTF-8Ëß£Á†ÅÂ§±Ë¥•Ôºå‰ΩøÁî®Latin-1:', error);
    return decodeLatin1(data);
  }
}

/**
 * Ê£ÄÊµãAUTOMATIC1111Ê†ºÂºè
 */
function isAutomatic1111Format(textData) {
  if (textData.keyword !== 'parameters') return false;
  const text = textData.text.toLowerCase();
  return text.includes('steps:') || text.includes('cfg scale:') || 
         text.includes('sampler:') || text.includes('negative prompt:');
}

/**
 * Ëß£ÊûêAUTOMATIC1111Êï∞ÊçÆ - Â¢ûÂº∫ÁâàÔºå‰∏ç‰ºöÊà™Êñ≠ÊèêÁ§∫ËØç
 */
function parseAutomatic1111Data(text) {
  const result = {
    positive: '',
    negative: '',
    parameters: {}
  };

  try {
    // Êü•ÊâæË¥üÂêëÊèêÁ§∫ËØçÂàÜÈöîÁ¨¶
    const negativePromptIndex = text.indexOf('Negative prompt:');
    
    if (negativePromptIndex !== -1) {
      // ÊèêÂèñÊ≠£ÂêëÊèêÁ§∫ËØç
      result.positive = text.substring(0, negativePromptIndex).trim();
      
      // ÊèêÂèñÂâ©‰ΩôÈÉ®ÂàÜ
      const afterNegative = text.substring(negativePromptIndex + 16); // 'Negative prompt:' ÈïøÂ∫¶
      
      // Êü•ÊâæÂèÇÊï∞ÈÉ®ÂàÜÁöÑÂºÄÂßã
      const parameterLines = afterNegative.split('\n');
      let negativePrompt = '';
      let parametersStartIndex = -1;
      
      for (let i = 0; i < parameterLines.length; i++) {
        const line = parameterLines[i].trim();
        if (looksLikeParameterLine(line)) {
          parametersStartIndex = i;
          break;
        } else if (line) {
          negativePrompt += (negativePrompt ? '\n' : '') + line;
        }
      }
      
      result.negative = negativePrompt.trim();
      
      // Ëß£ÊûêÂèÇÊï∞
      if (parametersStartIndex !== -1) {
        const paramText = parameterLines.slice(parametersStartIndex).join(', ');
        result.parameters = parseParameterString(paramText);
      }
    } else {
      // Ê≤°ÊúâË¥üÂêëÊèêÁ§∫ËØçÔºåÊü•ÊâæÂèÇÊï∞ÂàÜÈöî
      const lines = text.split('\n');
      let positiveLines = [];
      let parameterLines = [];
      let foundParameters = false;
      
      for (const line of lines) {
        const trimmedLine = line.trim();
        if (looksLikeParameterLine(trimmedLine)) {
          foundParameters = true;
          parameterLines.push(trimmedLine);
        } else if (!foundParameters && trimmedLine) {
          positiveLines.push(trimmedLine);
        } else if (foundParameters) {
          parameterLines.push(trimmedLine);
        }
      }
      
      result.positive = positiveLines.join(' ').trim();
      
      if (parameterLines.length > 0) {
        result.parameters = parseParameterString(parameterLines.join(', '));
      }
    }

  } catch (error) {
    console.warn('AUTOMATIC1111Ëß£ÊûêË≠¶Âëä:', error);
    result.positive = text; // Â§áÁî®ÊñπÊ°à
  }

  return result;
}

/**
 * Ê£ÄÊµãComfyUIÊ†ºÂºè
 */
function isComfyUIFormat(textData) {
  if (!['workflow', 'prompt'].includes(textData.keyword)) return false;
  try {
    const parsed = JSON.parse(textData.text);
    return parsed && typeof parsed === 'object';
  } catch {
    return false;
  }
}

/**
 * Ëß£ÊûêComfyUIÊï∞ÊçÆ - ÂÆåÊï¥ÊèêÂèñÔºå‰∏çÊà™Êñ≠
 */
function parseComfyUIData(text) {
  const result = {
    positive: '',
    negative: '',
    parameters: {}
  };

  try {
    const data = JSON.parse(text);
    
    if (Array.isArray(data?.nodes)) {
      // Â∑•‰ΩúÊµÅÊ†ºÂºè
      const prompts = extractPromptsFromComfyWorkflow(data);
      result.positive = prompts.positive;
      result.negative = prompts.negative;
      result.parameters.workflow = data;
    } else if (typeof data === 'object') {
      // ÊèêÁ§∫Ê†ºÂºè
      const prompts = extractPromptsFromComfyPrompt(data);
      result.positive = prompts.positive;
      result.negative = prompts.negative;
      result.parameters.prompt = data;
    }

  } catch (error) {
    console.warn('ComfyUIËß£ÊûêË≠¶Âëä:', error);
  }

  return result;
}

/**
 * ‰ªéComfyUIÂ∑•‰ΩúÊµÅÊèêÂèñÊèêÁ§∫ËØç - ‰øÆÂ§çÁâàÔºåÊî∂ÈõÜÊâÄÊúâÊñáÊú¨
 */
function extractPromptsFromComfyWorkflow(workflow) {
  const positivePrompts = [];
  const negativePrompts = [];

  for (const node of workflow.nodes || []) {
    if (['CLIPTextEncode', 'CLIPTextEncodeSDXL', 'ConditioningCombine'].includes(node.type)) {
      const text = node.widgets_values?.[0];
      if (text && typeof text === 'string' && text.trim()) {
        const cleanText = text.trim();
        
        // Ê†πÊçÆËäÇÁÇπÊ†áÈ¢òÊàñÂÜÖÂÆπÂà§Êñ≠Ê≠£Ë¥üÂêë
        const isNegative = isNegativePrompt(cleanText) || 
                         node.title?.toLowerCase().includes('negative') ||
                         node.title?.toLowerCase().includes('neg');
        
        if (isNegative) {
          negativePrompts.push(cleanText);
        } else {
          positivePrompts.push(cleanText);
        }
      }
    }
  }

  return {
    positive: mergePrompts(positivePrompts),
    negative: mergePrompts(negativePrompts)
  };
}

/**
 * ‰ªéComfyUIÊèêÁ§∫ÊèêÂèñÊèêÁ§∫ËØç
 */
function extractPromptsFromComfyPrompt(prompt) {
  const positivePrompts = [];
  const negativePrompts = [];

  for (const [nodeId, nodeData] of Object.entries(prompt)) {
    if (['CLIPTextEncode', 'CLIPTextEncodeSDXL'].includes(nodeData.class_type)) {
      const text = nodeData.inputs?.text;
      if (text && typeof text === 'string' && text.trim()) {
        const cleanText = text.trim();
        
        if (isNegativePrompt(cleanText)) {
          negativePrompts.push(cleanText);
        } else {
          positivePrompts.push(cleanText);
        }
      }
    }
  }

  return {
    positive: mergePrompts(positivePrompts),
    negative: mergePrompts(negativePrompts)
  };
}

/**
 * Êô∫ËÉΩÂêàÂπ∂ÊèêÁ§∫ËØç - ‰øùÊåÅÂÆåÊï¥ÊÄß
 */
function mergePrompts(prompts) {
  if (!prompts.length) return '';
  if (prompts.length === 1) return prompts[0];

  // Ê£ÄÊü•ÊòØÂê¶ÊúâÊòéÊòæÊõ¥ÈïøÊõ¥ÂÆåÊï¥ÁöÑÊèêÁ§∫ËØç
  const longest = prompts.reduce((max, current) => 
    current.length > max.length ? current : max);
  
  // Â¶ÇÊûúÊúÄÈïøÁöÑÊØîÂÖ∂‰ªñÁöÑÈïøÂæàÂ§öÔºåÈÄâÊã©ÂÆÉ
  const avgLength = prompts.reduce((sum, p) => sum + p.length, 0) / prompts.length;
  if (longest.length > avgLength * 2) {
    return longest;
  }
  
  // Âê¶ÂàôÊåâÈïøÂ∫¶ÊéíÂ∫èÔºå‰ºòÂÖàÈÄâÊã©ÈïøÁöÑ
  const sorted = [...prompts].sort((a, b) => b.length - a.length);
  return sorted[0];
}

/**
 * Ê£ÄÊµãNovelAIÊ†ºÂºè
 */
function isNovelAIFormat(textData) {
  if (!['Description', 'Comment'].includes(textData.keyword)) return false;
  try {
    const parsed = JSON.parse(textData.text);
    return parsed.prompt !== undefined || parsed.uc !== undefined;
  } catch {
    return false;
  }
}

/**
 * Ëß£ÊûêNovelAIÊï∞ÊçÆ
 */
function parseNovelAIData(text) {
  const result = {
    positive: '',
    negative: '',
    parameters: {}
  };

  try {
    const data = JSON.parse(text);
    result.positive = data.prompt || '';
    result.negative = data.uc || '';
    
    ['steps', 'scale', 'seed', 'sampler', 'width', 'height'].forEach(key => {
      if (data[key] !== undefined) {
        result.parameters[key] = data[key];
      }
    });

  } catch (error) {
    console.warn('NovelAIËß£ÊûêË≠¶Âëä:', error);
  }

  return result;
}

/**
 * ÊîπËøõÁöÑË¥üÂêëÊèêÁ§∫ËØçÊ£ÄÊµã
 */
function isNegativePrompt(text) {
  if (!text || typeof text !== 'string') return false;
  
  const lowerText = text.toLowerCase();
  
  const strongNegative = [
    'worst quality', 'bad anatomy', 'ugly', 'blurry', 'lowres',
    'bad hands', 'text', 'error', 'cropped', 'out of frame',
    'deformed', 'mutated', 'malformed', 'poorly drawn'
  ];

  return strongNegative.some(keyword => lowerText.includes(keyword));
}

/**
 * Ê£ÄÊµãÊèêÁ§∫ËØçÊ†∑ÂºèÊñáÊú¨
 */
function containsPromptLikeText(text) {
  if (!text || typeof text !== 'string' || text.length < 10) return false;
  
  const keywords = [
    'masterpiece', 'best quality', 'detailed', 'high resolution',
    'realistic', 'anime', 'portrait', 'landscape'
  ];

  const lowerText = text.toLowerCase();
  const hasKeywords = keywords.some(keyword => lowerText.includes(keyword));
  const hasCommas = text.includes(',') && text.split(',').length > 2;
  
  return hasKeywords || hasCommas;
}

/**
 * Ê£ÄÊµãÂèÇÊï∞Ë°å
 */
function looksLikeParameterLine(line) {
  const parameterPattern = /\b(Steps|Sampler|CFG scale|Seed|Size|Model|Clip skip|Denoising strength):/i;
  return parameterPattern.test(line);
}

/**
 * Ëß£ÊûêÂèÇÊï∞Â≠óÁ¨¶‰∏≤
 */
function parseParameterString(paramText) {
  const parameters = {};
  const paramPattern = /(\w+(?:\s+\w+)*)\s*:\s*([^,]+?)(?=,\s*\w+\s*:|$)/g;
  let match;

  while ((match = paramPattern.exec(paramText)) !== null) {
    const key = match[1].trim();
    const value = match[2].trim();
    
    const normalizedKey = normalizeParameterKey(key);
    parameters[normalizedKey] = parseParameterValue(value);
  }

  return parameters;
}

/**
 * Ê†áÂáÜÂåñÂèÇÊï∞ÈîÆÂêç
 */
function normalizeParameterKey(key) {
  const keyMap = {
    'steps': 'steps',
    'sampler': 'sampler',
    'cfg scale': 'cfgScale',
    'seed': 'seed',
    'size': 'size',
    'model hash': 'modelHash',
    'model': 'model',
    'clip skip': 'clipSkip',
    'denoising strength': 'denoisingStrength'
  };
  
  return keyMap[key.toLowerCase()] || key.replace(/\s+/g, '');
}

/**
 * Ëß£ÊûêÂèÇÊï∞ÂÄº
 */
function parseParameterValue(value) {
  const numValue = parseFloat(value);
  if (!isNaN(numValue) && isFinite(numValue)) {
    return Number.isInteger(numValue) ? parseInt(value) : numValue;
  }
  return value;
}

/**
 * EXIFÊï∞ÊçÆÊèêÂèñ
 */
async function extractFromEXIF(file) {
  try {
    const exifData = await exifr.parse(file, {
      userComment: true,
      imageDescription: true,
      software: true
    });

    if (!exifData) return null;

    const result = {
      type: 'EXIF',
      confidence: 'medium',
      data: {
        software: exifData.Software,
        description: exifData.ImageDescription,
        userComment: exifData.UserComment
      }
    };

    if (exifData.UserComment) {
      const aiData = parseUserComment(exifData.UserComment);
      if (aiData.positive || aiData.negative) {
        Object.assign(result.data, aiData);
        result.confidence = 'high';
      }
    }

    return result;
  } catch (error) {
    console.warn('EXIFÊèêÂèñÂ§±Ë¥•:', error);
    return null;
  }
}

/**
 * Ëß£ÊûêUserComment
 */
function parseUserComment(userComment) {
  const result = { positive: '', negative: '', parameters: {} };

  try {
    if (userComment.startsWith('{')) {
      const data = JSON.parse(userComment);
      result.positive = data.prompt || data.positive || '';
      result.negative = data.negativePrompt || data.negative || '';
      result.parameters = data.parameters || {};
    } else {
      result.positive = userComment;
    }
  } catch {
    result.positive = userComment;
  }

  return result;
}

/**
 * Ê†áÂáÜÂåñÊèêÂèñÁöÑÊï∞ÊçÆ
 */
function standardizeExtractedData(extractedData) {
  const standardized = {
    generationTool: 'Unknown',
    positive: '',
    negative: '',
    parameters: {}
  };

  const priorityOrder = ['PNG_Chunks', 'EXIF'];
  
  for (const source of priorityOrder) {
    const data = extractedData[source];
    if (!data) continue;

    if (data.data.generationTool) {
      standardized.generationTool = data.data.generationTool;
    }

    if (data.data.positive && !standardized.positive) {
      standardized.positive = data.data.positive;
    }

    if (data.data.negative && !standardized.negative) {
      standardized.negative = data.data.negative;
    }

    Object.assign(standardized.parameters, data.data.parameters || {});
  }

  return standardized;
}

