/**
 * æ ‡ç­¾èƒ¶å›Šç»„ä»¶
 */
const TagPill = ({ 
  tag, 
  index,
  isDisabled,
  onDelete, 
  onToggleDisabled,
  onAdjustWeight,
  onAdjustBrackets,
  onCopy,
  onFavorite,
  onTranslate,
  translatedTags,
  hoveredTag,
  setHoveredTag
}) => {
  const [translation, setTranslation] = useState('');
  const [isTranslating, setIsTranslating] = useState(false);

  // è§£ææ ‡ç­¾
  const parseTag = (tag) => {
    // æ£€æµ‹æƒé‡ (tag:1.2)
    const weightMatch = tag.match(/^(.+?):([0-9.]+)$/);
    if (weightMatch) {
      return {
        text: weightMatch[1],
        weight: parseFloat(weightMatch[2]),
        bracketType: 'none',
        brackets: 0
      };
    }
    
    // æ£€æµ‹æ‹¬å·ç±»å‹å’Œå±‚çº§
    let brackets = 0;
    let text = tag;
    let bracketType = 'none';
    
    // æ£€æµ‹åœ†æ‹¬å· ()
    if (text.startsWith('(') && text.endsWith(')')) {
      bracketType = 'round';
      while (text.startsWith('(') && text.endsWith(')')) {
        brackets++;
        text = text.slice(1, -1);
      }
    }
    // æ£€æµ‹èŠ±æ‹¬å· {}
    else if (text.startsWith('{') && text.endsWith('}')) {
      bracketType = 'curly';
      while (text.startsWith('{') && text.endsWith('}')) {
        brackets++;
        text = text.slice(1, -1);
      }
    }
    // æ£€æµ‹æ–¹æ‹¬å· []
    else if (text.startsWith('[') && text.endsWith(']')) {
      bracketType = 'square';
      while (text.startsWith('[') && text.endsWith(']')) {
        brackets++;
        text = text.slice(1, -1);
      }
    }
    
    return { text, weight: 1.0, bracketType, brackets };
  };

  const parsed = parseTag(tag);
  const isHovered = hoveredTag === index;

  // åŠ è½½ç¿»è¯‘
  useEffect(() => {
    if (parsed.text && !translatedTags[parsed.text]) {
      setIsTranslating(true);
      onTranslate(tag).then(result => {
        setTranslation(result);
        setIsTranslating(false);
      });
    } else if (translatedTags[parsed.text]) {
      setTranslation(translatedTags[parsed.text]);
    }
  }, [tag, translatedTags, onTranslate, parsed.text]);

  return (
    <div 
      className="relative group"
      onMouseEnter={() => setHoveredTag(index)}
      onMouseLeave={(e) => {
        // æ‰©å¤§åˆ¤å®šèŒƒå›´ï¼šæ£€æŸ¥é¼ æ ‡æ˜¯å¦çœŸçš„ç¦»å¼€äº†æ•´ä¸ªåŒºåŸŸ
        const rect = e.currentTarget.getBoundingClientRect();
        const x = e.clientX;
        const y = e.clientY;
        
        // æ‰©å¤§æ£€æµ‹èŒƒå›´20åƒç´ 
        const margin = 20;
        if (x < rect.left - margin || x > rect.right + margin || 
            y < rect.top - margin || y > rect.bottom + 120) { // åº•éƒ¨å¤šç•™120pxç»™ç¼–è¾‘æ 
          setHoveredTag(null);
        }
      }}
    >
      {/* ä¸»æ ‡ç­¾èƒ¶å›Š */}
      <div
        className={`inline-flex items-center gap-2 px-3 py-2 rounded-full text-sm font-medium transition-all duration-200 cursor-pointer ${
          isDisabled 
            ? 'bg-gray-200 text-gray-500 line-through opacity-60' 
            : 'bg-blue-100 text-blue-800 hover:bg-blue-200'
        }`}
      >
        <span className="break-all">{tag}</span>
        <button
          onClick={onDelete}
          className="flex-shrink-0 w-4 h-4 rounded-full bg-blue-600 text-white flex items-center justify-center hover:bg-red-500 transition-colors"
        >
          <X size={10} />
        </button>
      </div>
      
      {/* ä¸­æ–‡ç¿»è¯‘ */}
      {translation && (
        <div className="text-xs text-gray-500 mt-1 px-2">
          {isTranslating ? 'ç¿»è¯‘ä¸­...' : translation}
        </div>
      )}

      {/* æ‚¬åœç¼–è¾‘æ  */}
      {isHovered && (
        <div 
          className="absolute top-full left-0 mt-2 bg-white border border-gray-300 rounded-lg shadow-lg p-3 z-20 min-w-max"
          onMouseEnter={() => setHoveredTag(index)}
          onMouseLeave={() => setHoveredTag(null)}
        >
          <div className="flex flex-col gap-3">
            {/* ç¬¬ä¸€è¡Œï¼šæƒé‡è°ƒæ•´ */}
            <div className="flex items-center gap-1">
              <span className="text-xs text-gray-600 min-w-8">æƒé‡:</span>
              <button
                onClick={() => onAdjustWeight(-0.1)}
                className="w-6 h-6 rounded bg-gray-100 hover:bg-gray-200 flex items-center justify-center"
              >
                <ChevronLeft size={12} />
              </button>
              <span className="text-sm font-mono min-w-8 text-center">
                {parsed.weight.toFixed(1)}
              </span>
              <button
                onClick={() => onAdjustWeight(0.1)}
                className="w-6 h-6 rounded bg-gray-100 hover:bg-gray-200 flex items-center justify-center"
              >
                <ChevronRightIcon size={12} />
              </button>
            </div>

            {/* ç¬¬äºŒè¡Œï¼šæ‹¬å·ç±»å‹æ§åˆ¶ */}
            <div className="flex items-center gap-2">
              <span className="text-xs text-gray-600 min-w-8">æ‹¬å·:</span>
              
              {/* åœ†æ‹¬å· () - 1.1å€æƒé‡ */}
              <div className="flex items-center gap-1">
                <button
                  onClick={() => onAdjustBrackets('round', -1)}
                  className={`w-5 h-5 rounded text-xs flex items-center justify-center ${
                    parsed.bracketType === 'round' 
                      ? 'bg-orange-100 hover:bg-orange-200 text-orange-600' 
                      : 'bg-gray-100 hover:bg-gray-200 text-gray-400'
                  }`}
                  disabled={parsed.bracketType !== 'none' && parsed.bracketType !== 'round'}
                >
                  <Minus size={8} />
                </button>
                <div className={`px-2 py-1 rounded text-xs ${
                  parsed.bracketType === 'round' ? 'bg-orange-100 text-orange-700' : 'bg-gray-100 text-gray-500'
                }`}>
                  () {parsed.bracketType === 'round' ? parsed.brackets : 0}
                </div>
                <button
                  onClick={() => onAdjustBrackets('round', 1)}
                  className={`w-5 h-5 rounded text-xs flex items-center justify-center ${
                    (parsed.bracketType === 'none' || parsed.bracketType === 'round') && parsed.brackets < 5
                      ? 'bg-orange-100 hover:bg-orange-200 text-orange-600' 
                      : 'bg-gray-100 text-gray-400'
                  }`}
                  disabled={parsed.bracketType !== 'none' && parsed.bracketType !== 'round'}
                >
                  <Plus size={8} />
                </button>
              </div>

              {/* èŠ±æ‹¬å· {} - 1.05å€æƒé‡ */}
              <div className="flex items-center gap-1">
                <button
                  onClick={() => onAdjustBrackets('curly', -1)}
                  className={`w-5 h-5 rounded text-xs flex items-center justify-center ${
                    parsed.bracketType === 'curly' 
                      ? 'bg-yellow-100 hover:bg-yellow-200 text-yellow-600' 
                      : 'bg-gray-100 hover:bg-gray-200 text-gray-400'
                  }`}
                  disabled={parsed.bracketType !== 'none' && parsed.bracketType !== 'curly'}
                >
                  <Minus size={8} />
                </button>
                <div className={`px-2 py-1 rounded text-xs ${
                  parsed.bracketType === 'curly' ? 'bg-yellow-100 text-yellow-700' : 'bg-gray-100 text-gray-500'
                }`}>
                  {`{}`} {parsed.bracketType === 'curly' ? parsed.brackets : 0}
                </div>
                <button
                  onClick={() => onAdjustBrackets('curly', 1)}
                  className={`w-5 h-5 rounded text-xs flex items-center justify-center ${
                    (parsed.bracketType === 'none' || parsed.bracketType === 'curly') && parsed.brackets < 5
                      ? 'bg-yellow-100 hover:bg-yellow-200 text-yellow-600' 
                      : 'bg-gray-100 text-gray-400'
                  }`}
                  disabled={parsed.bracketType !== 'none' && parsed.bracketType !== 'curly'}
                >
                  <Plus size={8} />
                </button>
              </div>

              {/* æ–¹æ‹¬å· [] - 0.9å€æƒé‡ */}
              <div className="flex items-center gap-1">
                <button
                  onClick={() => onAdjustBrackets('square', -1)}
                  className={`w-5 h-5 rounded text-xs flex items-center justify-center ${
                    parsed.bracketType === 'square' 
                      ? 'bg-blue-100 hover:bg-blue-200 text-blue-600' 
                      : 'bg-gray-100 hover:bg-gray-200 text-gray-400'
                  }`}
                  disabled={parsed.bracketType !== 'none' && parsed.bracketType !== 'square'}
                >
                  <Minus size={8} />
                </button>
                <div className={`px-2 py-1 rounded text-xs ${
                  parsed.bracketType === 'square' ? 'bg-blue-100 text-blue-700' : 'bg-gray-100 text-gray-500'
                }`}>
                  [] {parsed.bracketType === 'square' ? parsed.brackets : 0}
                </div>
                <button
                  onClick={() => onAdjustBrackets('square', 1)}
                  className={`w-5 h-5 rounded text-xs flex items-center justify-center ${
                    (parsed.bracketType === 'none' || parsed.bracketType === 'square') && parsed.brackets < 5
                      ? 'bg-blue-100 hover:bg-blue-200 text-blue-600' 
                      : 'bg-gray-100 text-gray-400'
                  }`}
                  disabled={parsed.bracketType !== 'none' && parsed.bracketType !== 'square'}
                >
                  <Plus size={8} />
                </button>
              </div>
            </div>

            {/* ç¬¬ä¸‰è¡Œï¼šåŠŸèƒ½æŒ‰é’® */}
            <div className="flex items-center gap-1">
              <button
                onClick={onCopy}
                className="w-6 h-6 rounded bg-green-100 hover:bg-green-200 text-green-600 flex items-center justify-center"
                title="å¤åˆ¶"
              >
                <Copy size={12} />
              </button>
              <button
                onClick={onFavorite}
                className="w-6 h-6 rounded bg-red-100 hover:bg-red-200 text-red-600 flex items-center justify-center"
                title="æ”¶è—"
              >
                <Heart size={12} />
              </button>
              <button
                onClick={onToggleDisabled}
                className={`w-6 h-6 rounded flex items-center justify-center ${
                  isDisabled 
                    ? 'bg-blue-100 hover:bg-blue-200 text-blue-600' 
                    : 'bg-gray-100 hover:bg-gray-200 text-gray-600'
                }`}
                title={isDisabled ? "å¯ç”¨" : "ç¦ç”¨"}
              >
                {isDisabled ? <Eye size={12} /> : <EyeOff size={12} />}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

/**
 * æ ‡ç­¾å¡ç‰‡ç»„ä»¶
 */
const TagCard = ({ tag, onAdd, onToggleFavorite, isFavorited }) => (
  <div className="group bg-gradient-to-r from-gray-50 to-white border border-gray-200 rounded-lg p-4 hover:shadow-md hover:border-blue-300 transition-all duration-200 cursor-pointer">
    <div onClick={onAdd} className="flex-1">
      <div className="flex items-start justify-between mb-2">
        <div className="flex-1">
          <h4 className="font-medium text-gray-900 text-sm mb-1 group-hover:text-blue-700 transition-colors">
            {tag.en}
          </h4>
          <p className="text-xs text-blue-600 mb-2">
            {tag.cn}
          </p>
        </div>
        <button
          onClick={(e) => {
            e.stopPropagation();
            onToggleFavorite();
          }}
          className={`p-1 rounded-full transition-colors ${
            isFavorited 
              ? 'text-red-500 hover:bg-red-50' 
              : 'text-gray-400 hover:bg-gray-100 hover:text-red-500'
          }`}
        >
          <Heart size={14} fill={isFavorited ? 'currentColor' : 'none'} />
        </button>
      </div>
      
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <div className="flex items-center gap-1">
            <TrendingUp size={12} className="text-green-600" />
            <span className="text-xs text-green-600 font-medium">{tag.frequency}%</span>
          </div>
          <div className="w-12 bg-gray-200 rounded-full h-1">
            <div 
              className="bg-green-500 h-1 rounded-full transition-all" 
              style={{ width: `${tag.frequency}%` }}
            />
          </div>
        </div>
        
        <button
          onClick={onAdd}
          className="opacity-0 group-hover:opacity-100 transition-opacity text-xs bg-blue-600 text-white px-2 py-1 rounded-full hover:bg-blue-700"
        >
          <Plus size={10} className="inline mr-1" />
          æ·»åŠ 
        </button>
      </div>
    </div>
  </div>
);

export default PromptLibraryPage;
