import exifr from 'exifr';
import piexif from 'piexifjs';
import AdvancedPromptAnalyzer from './advancedPromptAnalyzer.js';

/**
 * ä¸“ä¸šçº§å›¾åƒå…ƒæ•°æ®æå–å™¨
 * åŸºäºstable-diffusion-inspectorã€auto1111-pnginfoç­‰å¼€æºé¡¹ç›®çš„æœ€ä½³å®è·µ
 * ä¸“é—¨é’ˆå¯¹AIå›¾åƒç”Ÿæˆå·¥å…·çš„å…ƒæ•°æ®æå–ä¼˜åŒ–
 */
export class ImageMetadataExtractor {
  constructor() {
    this.supportedFormats = ['jpg', 'jpeg', 'png', 'webp'];
    this.promptAnalyzer = new AdvancedPromptAnalyzer();
  }

  /**
   * ä¸»æå–æ–¹æ³• - ä½¿ç”¨å¤šé‡ç­–ç•¥å¹¶è¡Œæå–
   * @param {File} file - å›¾åƒæ–‡ä»¶
   * @returns {Promise<Object>} å®Œæ•´çš„æå–ç»“æœ
   */
  async extractMetadata(file) {
    console.log('ğŸš€ å¼€å§‹ä¸“ä¸šçº§å…ƒæ•°æ®æå–...');
    
    const startTime = Date.now();
    const result = {
      success: false,
      filename: file.name,
      timestamp: new Date().toISOString(),
      basicInfo: {
        size: file.size,
        sizeFormatted: this.formatFileSize(file.size),
        type: file.type,
        lastModified: new Date(file.lastModified).toISOString()
      },
      extractedData: {},
      standardizedData: null,
      extractionMethods: [],
      errors: []
    };

    try {
      // å¹¶è¡Œè¿è¡Œå¤šç§æå–æ–¹æ³•
      const extractionPromises = [
        this.extractFromEXIF(file),
        file.type === 'image/png' ? this.extractFromPNGChunks(file) : null,
        this.extractFromGenericMetadata(file)
      ].filter(Boolean);

      const extractionResults = await Promise.allSettled(extractionPromises);
      
      // æ•´åˆæ‰€æœ‰æˆåŠŸçš„æå–ç»“æœ
      extractionResults.forEach((promiseResult, index) => {
        if (promiseResult.status === 'fulfilled' && promiseResult.value) {
          const methodNames = ['EXIF', 'PNG_Chunks', 'Generic'];
          const methodName = methodNames[index];
          result.extractedData[methodName] = promiseResult.value;
          result.extractionMethods.push({
            method: methodName,
            success: true,
            confidence: promiseResult.value.confidence || 'medium'
          });
        } else if (promiseResult.status === 'rejected') {
          result.errors.push(`${methodNames[index]}: ${promiseResult.reason}`);
        }
      });

      // æ ‡å‡†åŒ–å’Œåˆå¹¶æ•°æ®
      result.standardizedData = this.standardizeExtractedData(result.extractedData);
      result.success = Object.keys(result.extractedData).length > 0;
      
      console.log(`âœ… æå–å®Œæˆ (${Date.now() - startTime}ms):`, {
        methods: result.extractionMethods.length,
        dataQuality: result.standardizedData?.generationTool || 'Unknown'
      });

      return result;
      
    } catch (error) {
      console.error('âŒ å…ƒæ•°æ®æå–å¤±è´¥:', error);
      result.errors.push(`æ€»ä½“é”™è¯¯: ${error.message}`);
      throw new Error(`å…ƒæ•°æ®æå–å¤±è´¥: ${error.message}`);
    }
  }

  /**
   * ä¸“ä¸šçº§PNGæ–‡æœ¬å—æå–å™¨ - å‚è€ƒauto1111-pnginfoçš„å®ç°
   * @param {File} file - PNGæ–‡ä»¶
   * @returns {Promise<Object>} PNGå…ƒæ•°æ®
   */
  async extractFromPNGChunks(file) {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      
      reader.onload = (e) => {
        try {
          const buffer = e.target.result;
          const view = new DataView(buffer);
          
          // PNGç­¾åéªŒè¯
          const pngSignature = [0x89, 0x50, 0x4E, 0x47, 0x0D, 0x0A, 0x1A, 0x0A];
          for (let i = 0; i < 8; i++) {
            if (view.getUint8(i) !== pngSignature[i]) {
              throw new Error('ä¸æ˜¯æœ‰æ•ˆçš„PNGæ–‡ä»¶');
            }
          }

          const chunks = this.parsePNGChunks(buffer);
          const metadata = this.extractAIMetadataFromChunks(chunks);
          
          resolve({
            type: 'PNG_Chunks',
            confidence: metadata.confidence || 'high',
            data: metadata,
            chunks: chunks.map(chunk => ({
              type: chunk.type,
              length: chunk.length
            }))
          });
          
        } catch (error) {
          reject(error);
        }
      };
      
      reader.onerror = () => reject(new Error('æ–‡ä»¶è¯»å–å¤±è´¥'));
      reader.readAsArrayBuffer(file);
    });
  }

  /**
   * é«˜çº§PNGå—è§£æå™¨ - ä¿®å¤ç‰¹æ®Šå­—ç¬¦å¤„ç†é—®é¢˜
   * @param {ArrayBuffer} buffer - PNGæ–‡ä»¶ç¼“å†²åŒº
   * @returns {Array} è§£æåçš„å—åˆ—è¡¨
   */
  parsePNGChunks(buffer) {
    const view = new DataView(buffer);
    const chunks = [];
    let offset = 8; // è·³è¿‡PNGç­¾å

    while (offset < buffer.byteLength - 8) {
      try {
        // è¯»å–å—é•¿åº¦ (big-endian)
        const length = view.getUint32(offset);
        offset += 4;

        // è¯»å–å—ç±»å‹
        const type = this.readChunkType(view, offset);
        offset += 4;

        // è¯»å–å—æ•°æ®
        const data = new Uint8Array(buffer, offset, length);
        offset += length;

        // è¯»å–CRC
        const crc = view.getUint32(offset);
        offset += 4;

        chunks.push({ type, length, data, crc });

        // å¦‚æœé‡åˆ°IENDå—ï¼Œåœæ­¢è§£æ
        if (type === 'IEND') break;
        
      } catch (error) {
        console.warn('PNGå—è§£æè­¦å‘Š:', error);
        break;
      }
    }

    return chunks;
  }

  /**
   * å®‰å…¨çš„å—ç±»å‹è¯»å–
   * @param {DataView} view - æ•°æ®è§†å›¾
   * @param {number} offset - åç§»é‡
   * @returns {string} å—ç±»å‹
   */
  readChunkType(view, offset) {
    const bytes = [];
    for (let i = 0; i < 4; i++) {
      bytes.push(view.getUint8(offset + i));
    }
    return String.fromCharCode(...bytes);
  }

  /**
   * ä»PNGå—ä¸­æå–AIå…ƒæ•°æ® - ä¸“é—¨å¤„ç†SDç”Ÿæˆçš„å›¾åƒ
   * @param {Array} chunks - PNGå—åˆ—è¡¨
   * @returns {Object} æå–çš„å…ƒæ•°æ®
   */
  extractAIMetadataFromChunks(chunks) {
    const textChunks = chunks.filter(chunk => 
      ['tEXt', 'iTXt', 'zTXt'].includes(chunk.type)
    );

    const metadata = {
      confidence: 'low',
      generationTool: 'Unknown',
      positive: '',
      negative: '',
      parameters: {}
    };

    for (const chunk of textChunks) {
      const textData = this.parseTextChunk(chunk);
      if (!textData.keyword || !textData.text) continue;

      // æ£€æµ‹ä¸åŒAIå·¥å…·çš„æ•°æ®æ ¼å¼
      if (this.isAutomatic1111Format(textData)) {
        Object.assign(metadata, this.parseAutomatic1111Data(textData.text));
        metadata.generationTool = 'AUTOMATIC1111';
        metadata.confidence = 'high';
      } else if (this.isComfyUIFormat(textData)) {
        Object.assign(metadata, this.parseComfyUIData(textData.text));
        metadata.generationTool = 'ComfyUI';
        metadata.confidence = 'high';
      } else if (this.isNovelAIFormat(textData)) {
        Object.assign(metadata, this.parseNovelAIData(textData.text));
        metadata.generationTool = 'NovelAI';
        metadata.confidence = 'high';
      } else if (this.containsPromptLikeText(textData.text)) {
        // é€šç”¨æç¤ºè¯æ£€æµ‹
        metadata.positive = textData.text;
        metadata.confidence = 'medium';
      }
    }

    return metadata;
  }

  /**
   * é«˜çº§æ–‡æœ¬å—è§£æå™¨ - æ­£ç¡®å¤„ç†æ‰€æœ‰ç¼–ç å’Œç‰¹æ®Šå­—ç¬¦
   * @param {Object} chunk - PNGæ–‡æœ¬å—
   * @returns {Object} è§£æåçš„æ–‡æœ¬æ•°æ®
   */
  parseTextChunk(chunk) {
    try {
      const data = chunk.data;
      
      if (chunk.type === 'tEXt') {
        return this.parseTEXt(data);
      } else if (chunk.type === 'iTXt') {
        return this.parseITXt(data);
      } else if (chunk.type === 'zTXt') {
        return this.parseZTXt(data);
      }
      
      return {};
    } catch (error) {
      console.warn('æ–‡æœ¬å—è§£æå¤±è´¥:', error);
      return {};
    }
  }

  /**
   * è§£ætEXtå— - Latin-1ç¼–ç 
   * @param {Uint8Array} data - å—æ•°æ®
   * @returns {Object} è§£æç»“æœ
   */
  parseTEXt(data) {
    // æŸ¥æ‰¾nullåˆ†éš”ç¬¦
    const nullIndex = data.indexOf(0);
    if (nullIndex === -1) return {};

    const keyword = this.decodeLatin1(data.slice(0, nullIndex));
    const text = this.decodeLatin1(data.slice(nullIndex + 1));

    return { keyword, text };
  }

  /**
   * è§£æiTXtå— - UTF-8ç¼–ç 
   * @param {Uint8Array} data - å—æ•°æ®
   * @returns {Object} è§£æç»“æœ
   */
  parseITXt(data) {
    try {
      const nullIndices = [];
      for (let i = 0; i < data.length; i++) {
        if (data[i] === 0) {
          nullIndices.push(i);
          if (nullIndices.length >= 4) break;
        }
      }

      if (nullIndices.length < 4) return {};

      const keyword = this.decodeUTF8(data.slice(0, nullIndices[0]));
      const compression = data[nullIndices[0] + 1];
      const text = this.decodeUTF8(data.slice(nullIndices[3] + 1));

      return { keyword, text, compression };
    } catch (error) {
      console.warn('iTXtè§£æå¤±è´¥:', error);
      return {};
    }
  }

  /**
   * è§£æzTXtå— - å‹ç¼©æ–‡æœ¬
   * @param {Uint8Array} data - å—æ•°æ®
   * @returns {Object} è§£æç»“æœ
   */
  parseZTXt(data) {
    try {
      const nullIndex = data.indexOf(0);
      if (nullIndex === -1) return {};

      const keyword = this.decodeLatin1(data.slice(0, nullIndex));
      const compression = data[nullIndex + 1];
      
      if (compression !== 0) {
        console.warn('ä¸æ”¯æŒçš„å‹ç¼©æ–¹æ³•:', compression);
        return { keyword, text: '[å‹ç¼©æ–‡æœ¬]' };
      }

      // è¿™é‡Œéœ€è¦zlibè§£å‹ç¼©ï¼Œæš‚æ—¶è¿”å›åŸå§‹æ•°æ®
      const compressedData = data.slice(nullIndex + 2);
      return { keyword, text: '[éœ€è¦è§£å‹ç¼©]', compressed: true };
    } catch (error) {
      console.warn('zTXtè§£æå¤±è´¥:', error);
      return {};
    }
  }

  /**
   * å®‰å…¨çš„Latin-1è§£ç 
   * @param {Uint8Array} data - æ•°æ®
   * @returns {string} è§£ç åçš„å­—ç¬¦ä¸²
   */
  decodeLatin1(data) {
    try {
      let result = '';
      for (let i = 0; i < data.length; i++) {
        result += String.fromCharCode(data[i]);
      }
      return result;
    } catch (error) {
      console.warn('Latin-1è§£ç å¤±è´¥:', error);
      return '';
    }
  }

  /**
   * å®‰å…¨çš„UTF-8è§£ç 
   * @param {Uint8Array} data - æ•°æ®
   * @returns {string} è§£ç åçš„å­—ç¬¦ä¸²
   */
  decodeUTF8(data) {
    try {
      return new TextDecoder('utf-8').decode(data);
    } catch (error) {
      console.warn('UTF-8è§£ç å¤±è´¥ï¼Œå°è¯•Latin-1:', error);
      return this.decodeLatin1(data);
    }
  }

  /**
   * æ£€æµ‹AUTOMATIC1111æ ¼å¼
   * @param {Object} textData - æ–‡æœ¬æ•°æ®
   * @returns {boolean} æ˜¯å¦ä¸ºA1111æ ¼å¼
   */
  isAutomatic1111Format(textData) {
    if (textData.keyword !== 'parameters') return false;
    const text = textData.text.toLowerCase();
    return text.includes('steps:') || text.includes('cfg scale:') || 
           text.includes('sampler:') || text.includes('negative prompt:');
  }

  /**
   * è§£æAUTOMATIC1111æ•°æ® - å¢å¼ºç‰ˆï¼Œå¤„ç†å¤æ‚æ ¼å¼
   * @param {string} text - å‚æ•°æ–‡æœ¬
   * @returns {Object} è§£æç»“æœ
   */
  parseAutomatic1111Data(text) {
    const result = {
      positive: '',
      negative: '',
      parameters: {}
    };

    try {
      // ä½¿ç”¨æ›´å¥å£®çš„è§£ææ–¹æ³•
      const lines = text.split('\n').map(line => line.trim()).filter(line => line);
      
      let currentSection = 'positive';
      let positiveLines = [];
      let negativeLines = [];
      let parameterLines = [];

      for (const line of lines) {
        if (line.startsWith('Negative prompt:')) {
          currentSection = 'negative';
          const negText = line.replace('Negative prompt:', '').trim();
          if (negText) negativeLines.push(negText);
        } else if (this.looksLikeParameterLine(line)) {
          currentSection = 'parameters';
          parameterLines.push(line);
        } else {
          if (currentSection === 'positive') {
            positiveLines.push(line);
          } else if (currentSection === 'negative') {
            negativeLines.push(line);
          }
        }
      }

      result.positive = positiveLines.join(' ').trim();
      result.negative = negativeLines.join(' ').trim();
      
      // è§£æå‚æ•°
      const paramText = parameterLines.join(', ');
      result.parameters = this.parseParameterString(paramText);

    } catch (error) {
      console.warn('AUTOMATIC1111è§£æè­¦å‘Š:', error);
      result.positive = text; // å¤‡ç”¨ï¼šå°†æ•´ä¸ªæ–‡æœ¬ä½œä¸ºæ­£å‘æç¤ºè¯
    }

    return result;
  }

  /**
   * æ£€æµ‹ComfyUIæ ¼å¼
   * @param {Object} textData - æ–‡æœ¬æ•°æ®
   * @returns {boolean} æ˜¯å¦ä¸ºComfyUIæ ¼å¼
   */
  isComfyUIFormat(textData) {
    if (!['workflow', 'prompt'].includes(textData.keyword)) return false;
    try {
      const parsed = JSON.parse(textData.text);
      return parsed && typeof parsed === 'object';
    } catch {
      return false;
    }
  }

  /**
   * è§£æComfyUIæ•°æ® - å¢å¼ºç‰ˆï¼Œå®Œæ•´æå–å·¥ä½œæµä¿¡æ¯
   * @param {string} text - JSONæ–‡æœ¬
   * @returns {Object} è§£æç»“æœ
   */
  parseComfyUIData(text) {
    const result = {
      positive: '',
      negative: '',
      parameters: {}
    };

    try {
      const data = JSON.parse(text);
      
      if (Array.isArray(data?.nodes)) {
        // å·¥ä½œæµæ ¼å¼
        result.parameters.workflow = data;
        const prompts = this.extractPromptsFromComfyWorkflow(data);
        result.positive = prompts.positive;
        result.negative = prompts.negative;
      } else if (typeof data === 'object') {
        // æç¤ºæ ¼å¼
        const prompts = this.extractPromptsFromComfyPrompt(data);
        result.positive = prompts.positive;
        result.negative = prompts.negative;
        result.parameters.prompt = data;
      }

    } catch (error) {
      console.warn('ComfyUIè§£æè­¦å‘Š:', error);
    }

    return result;
  }

  /**
   * ä»ComfyUIå·¥ä½œæµæå–æç¤ºè¯ - ä¿®å¤ç‰ˆ
   * @param {Object} workflow - å·¥ä½œæµå¯¹è±¡
   * @returns {Object} æå–çš„æç¤ºè¯
   */
  extractPromptsFromComfyWorkflow(workflow) {
    const positivePrompts = [];
    const negativePrompts = [];

    for (const node of workflow.nodes || []) {
      if (['CLIPTextEncode', 'CLIPTextEncodeSDXL'].includes(node.type)) {
        const text = node.widgets_values?.[0];
        if (text && typeof text === 'string' && text.trim()) {
          const cleanText = text.trim();
          if (this.isNegativePrompt(cleanText) || 
              node.title?.toLowerCase().includes('negative')) {
            negativePrompts.push(cleanText);
          } else {
            positivePrompts.push(cleanText);
          }
        }
      }
    }

    return {
      positive: this.selectBestPrompt(positivePrompts),
      negative: this.selectBestPrompt(negativePrompts)
    };
  }

  /**
   * ä»ComfyUIæç¤ºæå–æç¤ºè¯
   * @param {Object} prompt - æç¤ºå¯¹è±¡
   * @returns {Object} æå–çš„æç¤ºè¯
   */
  extractPromptsFromComfyPrompt(prompt) {
    const positivePrompts = [];
    const negativePrompts = [];

    for (const [nodeId, nodeData] of Object.entries(prompt)) {
      if (['CLIPTextEncode', 'CLIPTextEncodeSDXL'].includes(nodeData.class_type)) {
        const text = nodeData.inputs?.text;
        if (text && typeof text === 'string' && text.trim()) {
          const cleanText = text.trim();
          if (this.isNegativePrompt(cleanText)) {
            negativePrompts.push(cleanText);
          } else {
            positivePrompts.push(cleanText);
          }
        }
      }
    }

    return {
      positive: this.selectBestPrompt(positivePrompts),
      negative: this.selectBestPrompt(negativePrompts)
    };
  }

  /**
   * æ™ºèƒ½é€‰æ‹©æœ€ä½³æç¤ºè¯
   * @param {Array} prompts - æç¤ºè¯åˆ—è¡¨
   * @returns {string} æœ€ä½³æç¤ºè¯
   */
  selectBestPrompt(prompts) {
    if (!prompts.length) return '';
    if (prompts.length === 1) return prompts[0];

    // ä¼˜å…ˆé€‰æ‹©æœ€é•¿çš„æç¤ºè¯
    const longest = prompts.reduce((longest, current) => 
      current.length > longest.length ? current : longest, '');
    
    // å¦‚æœæœ€é•¿çš„æç¤ºè¯æ˜¾è‘—æ¯”å…¶ä»–é•¿ï¼Œé€‰æ‹©å®ƒ
    if (longest.length > 100) return longest;
    
    // å¦åˆ™åˆå¹¶æ‰€æœ‰æç¤ºè¯
    return prompts.join(', ');
  }

  /**
   * æ£€æµ‹NovelAIæ ¼å¼
   * @param {Object} textData - æ–‡æœ¬æ•°æ®
   * @returns {boolean} æ˜¯å¦ä¸ºNovelAIæ ¼å¼
   */
  isNovelAIFormat(textData) {
    if (!['Description', 'Comment'].includes(textData.keyword)) return false;
    try {
      const parsed = JSON.parse(textData.text);
      return parsed.prompt !== undefined || parsed.uc !== undefined;
    } catch {
      return false;
    }
  }

  /**
   * è§£æNovelAIæ•°æ®
   * @param {string} text - JSONæ–‡æœ¬
   * @returns {Object} è§£æç»“æœ
   */
  parseNovelAIData(text) {
    const result = {
      positive: '',
      negative: '',
      parameters: {}
    };

    try {
      const data = JSON.parse(text);
      result.positive = data.prompt || '';
      result.negative = data.uc || '';
      
      // æå–å‚æ•°
      ['steps', 'scale', 'seed', 'sampler', 'width', 'height'].forEach(key => {
        if (data[key] !== undefined) {
          result.parameters[key] = data[key];
        }
      });

    } catch (error) {
      console.warn('NovelAIè§£æè­¦å‘Š:', error);
    }

    return result;
  }

  /**
   * æ”¹è¿›çš„è´Ÿå‘æç¤ºè¯æ£€æµ‹
   * @param {string} text - æ–‡æœ¬
   * @returns {boolean} æ˜¯å¦ä¸ºè´Ÿå‘æç¤ºè¯
   */
  isNegativePrompt(text) {
    if (!text || typeof text !== 'string') return false;
    
    const lowerText = text.toLowerCase();
    
    // å¼ºè´Ÿå‘å…³é”®è¯
    const strongNegative = [
      'worst quality', 'bad anatomy', 'ugly', 'blurry', 'lowres',
      'bad hands', 'text', 'error', 'cropped', 'out of frame',
      'deformed', 'mutated', 'malformed', 'poorly drawn'
    ];

    // æ£€æŸ¥æ˜¯å¦åŒ…å«å¼ºè´Ÿå‘å…³é”®è¯
    const hasStrongNegative = strongNegative.some(keyword => 
      lowerText.includes(keyword));

    if (hasStrongNegative) return true;

    // å¼±è´Ÿå‘å…³é”®è¯
    const weakNegative = [
      'bad', 'worst', 'low', 'poor', 'ugly', 'error', 'wrong'
    ];

    const negativeCount = weakNegative.filter(keyword => 
      lowerText.includes(keyword)).length;
    
    const totalWords = text.split(/\s+/).length;
    const negativeRatio = negativeCount / totalWords;

    // å¦‚æœè´Ÿå‘è¯æ±‡æ¯”ä¾‹è¶…è¿‡60%ï¼Œåˆ¤å®šä¸ºè´Ÿå‘æç¤ºè¯
    return negativeRatio > 0.6;
  }

  /**
   * æ£€æµ‹æ˜¯å¦åŒ…å«æç¤ºè¯æ ·å¼çš„æ–‡æœ¬
   * @param {string} text - æ–‡æœ¬
   * @returns {boolean} æ˜¯å¦åƒæç¤ºè¯
   */
  containsPromptLikeText(text) {
    if (!text || typeof text !== 'string' || text.length < 10) return false;
    
    const keywords = [
      'masterpiece', 'best quality', 'detailed', 'high resolution',
      'realistic', 'anime', 'portrait', 'landscape', 'character'
    ];

    const lowerText = text.toLowerCase();
    const hasKeywords = keywords.some(keyword => lowerText.includes(keyword));
    const hasCommas = text.includes(',') && text.split(',').length > 2;
    
    return hasKeywords || hasCommas;
  }

  /**
   * æ£€æµ‹æ˜¯å¦ä¸ºå‚æ•°è¡Œ
   * @param {string} line - æ–‡æœ¬è¡Œ
   * @returns {boolean} æ˜¯å¦ä¸ºå‚æ•°è¡Œ
   */
  looksLikeParameterLine(line) {
    const parameterPattern = /\b(Steps|Sampler|CFG scale|Seed|Size|Model|Clip skip|Denoising strength):/i;
    return parameterPattern.test(line);
  }

  /**
   * è§£æå‚æ•°å­—ç¬¦ä¸²
   * @param {string} paramText - å‚æ•°æ–‡æœ¬
   * @returns {Object} è§£æåçš„å‚æ•°
   */
  parseParameterString(paramText) {
    const parameters = {};
    
    // ä½¿ç”¨æ­£åˆ™è¡¨è¾¾å¼åŒ¹é…å‚æ•°
    const paramPattern = /(\w+(?:\s+\w+)*)\s*:\s*([^,]+?)(?=,\s*\w+\s*:|$)/g;
    let match;

    while ((match = paramPattern.exec(paramText)) !== null) {
      const key = match[1].trim();
      const value = match[2].trim();
      
      // æ ‡å‡†åŒ–å‚æ•°å
      const normalizedKey = this.normalizeParameterKey(key);
      parameters[normalizedKey] = this.parseParameterValue(value);
    }

    return parameters;
  }

  /**
   * æ ‡å‡†åŒ–å‚æ•°é”®å
   * @param {string} key - åŸå§‹é”®å
   * @returns {string} æ ‡å‡†åŒ–åçš„é”®å
   */
  normalizeParameterKey(key) {
    const keyMap = {
      'steps': 'steps',
      'sampler': 'sampler',
      'cfg scale': 'cfgScale',
      'seed': 'seed',
      'size': 'size',
      'model hash': 'modelHash',
      'model': 'model',
      'clip skip': 'clipSkip',
      'denoising strength': 'denoisingStrength'
    };
    
    return keyMap[key.toLowerCase()] || key.replace(/\s+/g, '');
  }

  /**
   * è§£æå‚æ•°å€¼
   * @param {string} value - å‚æ•°å€¼
   * @returns {any} è§£æåçš„å€¼
   */
  parseParameterValue(value) {
    // å°è¯•è§£æä¸ºæ•°å­—
    const numValue = parseFloat(value);
    if (!isNaN(numValue) && isFinite(numValue)) {
      return Number.isInteger(numValue) ? parseInt(value) : numValue;
    }
    
    return value;
  }

  /**
   * EXIFæ•°æ®æå–
   * @param {File} file - æ–‡ä»¶
   * @returns {Promise<Object>} EXIFæ•°æ®
   */
  async extractFromEXIF(file) {
    try {
      const exifData = await exifr.parse(file, {
        userComment: true,
        imageDescription: true,
        software: true,
        make: true,
        model: true
      });

      if (!exifData) return null;

      const result = {
        type: 'EXIF',
        confidence: 'medium',
        data: {
          software: exifData.Software,
          make: exifData.Make,
          model: exifData.Model,
          description: exifData.ImageDescription,
          userComment: exifData.UserComment
        }
      };

      // å°è¯•ä»UserCommentä¸­æå–AIæ•°æ®
      if (exifData.UserComment) {
        const aiData = this.parseUserComment(exifData.UserComment);
        if (aiData.positive || aiData.negative) {
          Object.assign(result.data, aiData);
          result.confidence = 'high';
        }
      }

      return result;
    } catch (error) {
      console.warn('EXIFæå–å¤±è´¥:', error);
      return null;
    }
  }

  /**
   * è§£æUserCommentä¸­çš„AIæ•°æ®
   * @param {string} userComment - ç”¨æˆ·æ³¨é‡Š
   * @returns {Object} è§£æçš„AIæ•°æ®
   */
  parseUserComment(userComment) {
    const result = { positive: '', negative: '', parameters: {} };

    try {
      // å°è¯•JSONè§£æ
      if (userComment.startsWith('{')) {
        const data = JSON.parse(userComment);
        result.positive = data.prompt || data.positive || '';
        result.negative = data.negativePrompt || data.negative || '';
        result.parameters = data.parameters || {};
      } else {
        // ä½œä¸ºçº¯æ–‡æœ¬å¤„ç†
        result.positive = userComment;
      }
    } catch {
      result.positive = userComment;
    }

    return result;
  }

  /**
   * é€šç”¨å…ƒæ•°æ®æå–
   * @param {File} file - æ–‡ä»¶
   * @returns {Promise<Object>} é€šç”¨å…ƒæ•°æ®
   */
  async extractFromGenericMetadata(file) {
    // è¿™é‡Œå¯ä»¥æ·»åŠ å…¶ä»–æ ¼å¼çš„æ”¯æŒ
    return null;
  }

  /**
   * æ ‡å‡†åŒ–æå–çš„æ•°æ®
   * @param {Object} extractedData - æå–çš„åŸå§‹æ•°æ®
   * @returns {Object} æ ‡å‡†åŒ–åçš„æ•°æ®
   */
  standardizeExtractedData(extractedData) {
    const standardized = {
      generationTool: 'Unknown',
      positive: '',
      negative: '',
      parameters: {},
      quality: null
    };

    // ä¼˜å…ˆçº§ï¼šPNG_Chunks > EXIF > Generic
    const priorityOrder = ['PNG_Chunks', 'EXIF', 'Generic'];
    
    for (const source of priorityOrder) {
      const data = extractedData[source];
      if (!data) continue;

      if (data.data.generationTool) {
        standardized.generationTool = data.data.generationTool;
      }

      if (data.data.positive && !standardized.positive) {
        standardized.positive = data.data.positive;
      }

      if (data.data.negative && !standardized.negative) {
        standardized.negative = data.data.negative;
      }

      Object.assign(standardized.parameters, data.data.parameters || {});
    }

    // è´¨é‡è¯„ä¼°
    standardized.quality = this.assessDataQuality(standardized);

    return standardized;
  }

  /**
   * è¯„ä¼°æ•°æ®è´¨é‡
   * @param {Object} data - æ ‡å‡†åŒ–æ•°æ®
   * @returns {Object} è´¨é‡è¯„ä¼°
   */
  assessDataQuality(data) {
    let score = 0;
    const factors = [];

    // æç¤ºè¯è´¨é‡è¯„åˆ†
    if (data.positive) {
      score += Math.min(data.positive.length / 100, 0.3);
      factors.push('positive_prompt');
    }

    if (data.negative) {
      score += 0.2;
      factors.push('negative_prompt');
    }

    // å‚æ•°å®Œæ•´æ€§è¯„åˆ†
    const importantParams = ['steps', 'cfgScale', 'sampler', 'seed'];
    const paramScore = importantParams.filter(param => 
      data.parameters[param] !== undefined).length / importantParams.length;
    score += paramScore * 0.3;

    // å·¥å…·è¯†åˆ«åŠ åˆ†
    if (data.generationTool !== 'Unknown') {
      score += 0.2;
      factors.push('tool_detected');
    }

    return {
      overallScore: Math.min(score, 1),
      promptEffectiveness: data.positive ? Math.min(data.positive.length / 200, 1) : 0,
      parameterOptimization: paramScore,
      factors
    };
  }

  /**
   * æ ¼å¼åŒ–æ–‡ä»¶å¤§å°
   * @param {number} bytes - å­—èŠ‚æ•°
   * @returns {string} æ ¼å¼åŒ–åçš„å¤§å°
   */
  formatFileSize(bytes) {
    const units = ['B', 'KB', 'MB', 'GB'];
    let size = bytes;
    let unitIndex = 0;

    while (size >= 1024 && unitIndex < units.length - 1) {
      size /= 1024;
      unitIndex++;
    }

    return `${size.toFixed(2)} ${units[unitIndex]}`;
  }
}

